INSERT INTO `posts` (`post_id`, `title`, `create_at`, `edit_at`, `body`, `tags`, `views`, `replys`, `status`, `user_id`, `topic_id`, `mitopic_id`) VALUES
(85, 'Quy định Về Bài Viết Trong Diễn Đàn', '2020-11-03 07:13:19', '2020-11-03 07:24:20', '&lt;h2&gt;Nhằm n&acirc;ng cao chất lượng b&agrave;i viết trong forum, hạn chế tối đa c&aacute;c b&agrave;i r&aacute;c, bạn h&atilde;y l&agrave;m theo những quy định sau đ&acirc;y, nếu vi phạm b&agrave;i viết của bạn c&oacute; thể sẽ bị chuyển sang box kh&aacute;c hoặc bị x&oacute;a.&lt;br&gt;&amp;nbsp;&lt;/h2&gt;&lt;p&gt;&lt;strong&gt;QUY ĐỊNH CHUNG KHI GỬI B&Agrave;I&lt;/strong&gt;&lt;br&gt;​&lt;/p&gt;&lt;p&gt;1. &lt;strong&gt;Gửi b&agrave;i viết đ&uacute;ng phụ mục diễn đ&agrave;n (box)&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;. Nếu t&ecirc;n box kh&ocirc;ng m&ocirc; tả nhiều về nội dung box th&igrave; bạn n&ecirc;n xem thử một v&agrave;i b&agrave;i viết trong box đ&oacute; để xem nội dung b&agrave;i m&igrave;nh định gửi trong đ&oacute; c&oacute; đ&uacute;ng box kh&ocirc;ng.&lt;/p&gt;&lt;p&gt;2. &lt;strong&gt;Sử dụng tiếng Việt c&oacute; dấu. Kh&ocirc;ng viết kiểu như&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;: &quot;Chao ban, minh la thanh vien moi&quot;. Cũng kh&ocirc;ng bỏ dấu kiểu VIQR: &quot;Chao ca\'c ba.n, minh la thanh vie^n mo*\'i&quot;. Những b&agrave;i viết n&agrave;y sẽ bị x&oacute;a ngay lập tức.&lt;/p&gt;&lt;p&gt;3. &lt;strong&gt;Trước khi thắc mắc g&igrave; h&atilde;y thử t&igrave;m kiếm&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;. Vấn đề của bạn c&oacute; thể đ&atilde; được thảo luận/trả lời trước đ&oacute;.&amp;nbsp;&lt;/p&gt;&lt;p&gt;4. &lt;strong&gt;Sử dụng ti&ecirc;u đề ngắn gọn v&agrave; li&ecirc;n quan trực tiếp đến nội dung b&agrave;i viết&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;. Kh&ocirc;ng n&ecirc;n gửi c&aacute;c b&agrave;i viết ti&ecirc;u đề chung chung kiểu như &quot;Gi&uacute;p m&igrave;nh với&quot;, &quot;Hay lắm&quot;, &quot;Truyện cười&quot;. Ngo&agrave;i ra, kh&ocirc;ng viết IN HOA to&agrave;n bộ ti&ecirc;u đề.&lt;/p&gt;&lt;p&gt;5. &lt;strong&gt;Trả lời b&agrave;i viết của người kh&aacute;c đ&uacute;ng vấn đề đang thảo luận&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;. B&agrave;i trả lời phải li&ecirc;n quan trực tiếp tới vấn đề được thảo luận trong chủ đề đ&oacute;. Nếu kh&ocirc;ng li&ecirc;n quan, bạn h&atilde;y gửi b&agrave;i viết th&agrave;nh một chủ đề mới.&lt;/p&gt;&lt;p&gt;6. &lt;strong&gt;Kh&ocirc;ng sử dụng k&iacute;ch cỡ font chữ lớn trong b&agrave;i viết&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;. Nếu l&agrave; một b&agrave;i viết với nội dung b&igrave;nh thường th&igrave; bạn n&ecirc;n để k&iacute;ch cỡ b&igrave;nh thường (tức kh&ocirc;ng d&ugrave;ng thẻ &quot;size&quot;). Bạn c&oacute; thể d&ugrave;ng cỡ chữ lớn hơn với thẻ &quot;size&quot; nhưng kh&ocirc;ng được vượt qu&aacute; mức 5, v&agrave; chỉ d&ugrave;ng size 5 với những đoạn cần ch&uacute; &yacute;, những đoạn d&ugrave;ng hiệu ứng đặc biệt...&lt;/p&gt;&lt;p&gt;7. &lt;strong&gt;Kh&ocirc;ng sử dụng font chữ m&agrave;u đỏ&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;(ngoại trừ n&ecirc;u c&aacute;c d&ograve;ng, title nổi bật). Hạn chế d&ugrave;ng qu&aacute; nhiều m&agrave;u l&agrave;m kh&oacute; nh&igrave;n b&agrave;i viết.&lt;/p&gt;&lt;p&gt;8. &lt;strong&gt;Viết chữ trong 1 nền t&ugrave;y chọn c&oacute; m&agrave;u&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;vấn đề n&agrave;y chỉ được &aacute;p dụng đối với những c&acirc;u , từ mang t&iacute;nh đặc biệt cần nhấn mạnh trong 1 b&agrave;i viết để người đọc /người d&ugrave;ng tr&ecirc;n diễn đ&agrave;n ch&uacute; &yacute; hơn . Ko sử dụng cho nguy&ecirc;n b&agrave;i hoặc 1 đoạn text d&agrave;i nhằm mục đ&iacute;ch trang tr&iacute; cho b&agrave;i viết.&lt;/p&gt;&lt;p&gt;9. &lt;strong&gt;B&agrave;i viết cần từ 200 chữ trở l&ecirc;n&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;, cần c&oacute; h&igrave;nh ảnh minh họa (kh&ocirc;ng bắc buộc nhưng cần th&ecirc;m h&igrave;nh ảnh để dễ h&igrave;nh dung v&agrave; người xem c&oacute; cảm t&igrave;nh hơn với b&agrave;i viết).&lt;/p&gt;&lt;p&gt;10. Mọi b&agrave;i viết mang t&iacute;nh &lt;strong&gt;spam&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;sẽ bị ban m&agrave; kh&ocirc;ng cần th&ocirc;ng b&aacute;o.&lt;/p&gt;&lt;p&gt;Nguồn : Vn-Zoom&lt;/p&gt;', 'badge badge-danger Warning', 5, 0, 0, 2, 38, NULL),
(86, 'Thông báo - Không đăng tải nội dung vi phạm bản quyền', '2020-11-03 07:18:38', '2020-11-03 07:23:28', '&lt;p&gt;K&iacute;nh gửi anh em th&agrave;nh vi&ecirc;n diễn đ&agrave;n,&lt;br&gt;&lt;br&gt;Lu&ocirc;n t&ocirc;n trọng sự đ&oacute;ng g&oacute;p của anh em th&agrave;nh vi&ecirc;n sinh hoạt tại diễn đ&agrave;n, đ&oacute; l&agrave; t&agrave;i sản v&ocirc; c&ugrave;ng qu&yacute; gi&aacute; m&agrave; c&aacute;c bạn đ&atilde; tạo ra. Nhưng đi đ&ocirc;i với việc tạo ra c&aacute;c nội dung c&oacute; &iacute;ch th&igrave; diễn đ&agrave;n cũng phải đối mặt đến một số nội dung mang t&iacute;nh bản quyền b&ecirc;n thứ ba m&agrave; hầu như tất cả ch&uacute;ng ta đều biết.&lt;br&gt;V&igrave; vậy diễn đ&agrave;n diễn đ&agrave;n ch&iacute;nh thức th&ocirc;ng b&aacute;o&lt;strong&gt; cấm&lt;/strong&gt; c&aacute;c loại nội dung sau:&lt;br&gt;&lt;br&gt;+ Chia sẻ nội dung, sản phẩm Cr@ck&lt;br&gt;+ Chia sẻ Key (kh&ocirc;ng thuộc sở hữu c&aacute; nh&acirc;n)&lt;br&gt;+ Tuy&ecirc;n truyền, cổ su&yacute; cho việc h@ck, Cr@ck, bẻ kh&oacute;a...vv&lt;br&gt;&lt;br&gt;Dưới mỗi b&agrave;i viết về chia sẻ về sản phẩm &lt;strong&gt;bản quyền&lt;/strong&gt; c&aacute;c bạn cần copy nội dung sau ph&iacute;a dưới b&agrave;i:&lt;br&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;blockquote&gt;&lt;p&gt;Ch&uacute;ng t&ocirc;i t&ocirc;n trọng sản phẩm bản quyền, nếu sản phẩm thuộc sở hữu bản quyền c&aacute; nh&acirc;n vui l&ograve;ng nhắn tin qua hộp thư. Please message me if this product claim your copyright.&lt;/p&gt;&lt;/blockquote&gt;&lt;p&gt;&lt;strong&gt;TIPS:&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;Anh em lưu &yacute; l&agrave; kh&ocirc;ng d&ugrave;ng từ [full cờ rắc, full key] v&agrave;o nội dung b&agrave;i viết, v&igrave; một diễn đ&agrave;n diễn đ&agrave;n&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;12 năm qua n&oacute;i kh&ocirc;ng với c&aacute;c thứ n&agrave;y&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;Ch&uacute;ng ta c&oacute; nhiều c&aacute;ch để giải quyết nhưng đừng thể hiện ra b&ecirc;n ngo&agrave;i nội dung (chức năng nhắn tin ri&ecirc;ng cho chủ topic l&agrave; một c&aacute;ch) n&ecirc;n l&agrave; h&atilde;y tận dụng triệt để nh&eacute;.&lt;/p&gt;&lt;p&gt;Nh&acirc;n loại đ&atilde; tiến tới hiệp ước t&ocirc;n trọng &lt;strong&gt;BẢN QUYỀN&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;V&iacute; dụ:&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Phần mềm:&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;Chia sẻ phần mềm th&igrave; chỉ cần t&ecirc;n phần mềm + hướng dẫn sử dụng, c&ograve;n lại th&igrave; anh em c&oacute; thể tự hiểu với nhau (&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://vn-z.vn/threads/du-meter-7-30-full-phan-mem-kiem-tra-toc-do-mang-internet.6581/post-34070&quot;&gt;C&ocirc;ng cụ Internet DU Meter 7.30 Full - Phần mềm kiểm tra tốc độ mạng Internet!&lt;/a&gt;&lt;/p&gt;&lt;p&gt;)&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Phim:&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;T&ecirc;n phim + review c&ograve;n muốn hỏi g&igrave; th&ecirc;m th&igrave;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;inbox để chủ thớt&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;giới thiệu rạp chẳng hạn.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Game:&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;T&ecirc;n game + giới thiệu or review c&ograve;n muốn hỏi g&igrave; th&ecirc;m th&igrave;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;inbox chủ thớt&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;để giới thiệu chỗ mua game chẳng hạn.&lt;/p&gt;&lt;p&gt;Anh em h&atilde;y l&agrave;m quen thực hiện dần cho đồng bộ nội dung nh&eacute;.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;VI:&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;Đối với đơn vị khiếu nại vi phạm bản quyền tr&ecirc;n diễn đ&agrave;n vui l&ograve;ng xem v&agrave; đăng tải tại link b&ecirc;n dưới&lt;/p&gt;&lt;p&gt;&lt;strong&gt;EN:&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;Please claim content copyright visit the link below:&lt;/p&gt;&lt;p&gt;Tham khảo : Vn-Zoom&lt;/p&gt;', 'badge badge-success Share', 7, 0, 0, 2, 37, NULL),
(87, 'Chàng trai thủ khoa chọn ĐH Thủy lợi từ hơn 1 năm trước', '2020-11-03 07:28:51', NULL, '&lt;p&gt;Đến từ huyện &Acirc;n Thi, tỉnh Hưng Y&ecirc;n, Lương Quang Trường l&agrave; t&acirc;n thủ khoa tuyển sinh năm 2020 của Trường Đại học Thủy lợi với số điểm x&eacute;t tuyển gần chạm mức 28 điểm.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/2020/Thang11/truong2.JPG?ver=2020-11-02-202131-957&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;i&gt;Ch&agrave;ng trai thủ khoa gi&agrave;nh suất học bổng to&agrave;n phần&lt;/i&gt;&lt;/p&gt;&lt;p&gt;Y&ecirc;u th&iacute;ch C&ocirc;ng nghệ th&ocirc;ng tin từ l&acirc;u. Với ước mơ trở th&agrave;nh một lập tr&igrave;nh vi&ecirc;n đ&atilde; th&ocirc;i th&uacute;c ch&agrave;ng trai Lương Quang Trường học tập thật tốt. Ba năm học cấp 3 em lu&ocirc;n l&agrave; học sinh giỏi, đặc biệt l&agrave; c&aacute;c m&ocirc;n học khối tự nhi&ecirc;n. Năm lớp 12, Quang Trường c&ograve;n tham gia cuộc thi học sinh giỏi to&agrave;n tỉnh Hưng Y&ecirc;n v&agrave; đạt giải khuyến kh&iacute;ch m&ocirc;n Vật L&yacute;.&lt;/p&gt;&lt;p&gt;Đứng trước ngưỡng cửa đại học, Trường cho biết &ldquo;em kh&ocirc;ng cần qu&aacute; nhiều thời gian để c&acirc;n nhắc ng&agrave;nh học của m&igrave;nh l&agrave; ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin&rdquo;. C&aacute;i đ&iacute;ch của ch&agrave;ng trai trẻ l&agrave; quyết t&acirc;m v&agrave;o được trường đại học m&agrave; m&igrave;nh mơ ước ch&iacute;nh l&agrave; Trường Đại học Thủy lợi.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/2020/Thang11/truong1.JPG?ver=2020-11-02-202131-517&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;i&gt;Lương Quang Trường trong ng&agrave;y đầu nhập học&lt;/i&gt;&lt;/p&gt;&lt;p&gt;Khi được hỏi l&yacute; do n&agrave;o để em kh&ocirc;ng phải đắn đ&oacute; suy nghĩ khi lựa chọn duy nhất Trường Đại học Thủy lợi ngay đợt đăng k&yacute; đầu ti&ecirc;n với 4 nguyện vọng cho 4 ng&agrave;nh kh&aacute;c nhau của trường? thậm ch&iacute; khi biết điểm thi THPT với số điểm cao( gần 28 điểm ba m&ocirc;n) Quang Trường vẫn kh&ocirc;ng đổi nguyện vọng m&agrave; r&uacute;t số nguyện vọng của m&igrave;nh xuống con số 3, bạn tiết lộ &ldquo;em may mắn được tiếp x&uacute;c với c&aacute;c thầy c&ocirc; Trường Đại học Thủy lợi từ hơn 1 năm trước v&igrave; nh&agrave; em c&aacute;ch cơ sở Phố Hiến chưa đến mười c&acirc;y số, c&aacute;c thầy c&ocirc; rất nhiệt t&igrave;nh, t&acirc;m huyết. Đặc biệt cơ sở vật chất của trường rất tốt, rất khang trang n&ecirc;n em tin m&igrave;nh lựa chọn đ&uacute;ng&rdquo;.&lt;/p&gt;&lt;p&gt;Khi được hỏi bố mẹ em c&oacute; lời khuy&ecirc;n g&igrave; th&ecirc;m cho lựa chọn của em hay kh&ocirc;ng? ch&agrave;ng trai thư sinh với nụ cười lu&ocirc;n nở tr&ecirc;n m&ocirc;i t&acirc;m sự &ldquo; Bố em mất sớm, mẹ rất ủng hộ sự lựa chọn của em, Trường Đại học Thủy lợi l&agrave; một trường đại học c&oacute; uy t&iacute;n, kh&aacute; nhiều người ở địa phương em đang theo học ở đ&acirc;y n&ecirc;n mẹ em, thầy c&ocirc; gi&aacute;o rất ủng hộ em&rdquo;. Ước mơ của Quang Trường khi học xong ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin em sẽ c&oacute; thể kh&aacute;m ph&aacute; về c&ocirc;ng nghệ, t&igrave;m hiểu về chương tr&igrave;nh m&aacute;y t&iacute;nh, nghi&ecirc;n cứu về tr&iacute; tuệ nh&acirc;n tạo v&agrave; điện to&aacute;n đ&aacute;m m&acirc;y.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/2020/Thang11/truong3.JPG?ver=2020-11-02-202131-987&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;i&gt;T&acirc;n sinh vi&ecirc;n K62 ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin ph&aacute;t biểu trong lễ khai giảng&lt;/i&gt;&lt;/p&gt;&lt;p&gt;Được biết, với danh hiệu thủ khoa đầu v&agrave;o năm 2020 của Trường Đại học Thủy lợi, Quang Trường ch&iacute;nh thức nhận được một suất học bổng to&agrave;n phần cho to&agrave;n kh&oacute;a học với mức 90 triệu đồng, c&ugrave;ng c&aacute;c phần thưởng của c&aacute;c cựu sinh vi&ecirc;n, nh&agrave; t&agrave;i trợ. Với số tiền học bổng nhận được, Quang Trường sẽ d&agrave;nh một phần để chữa bệnh cho mẹ (mẹ em bị rất nhiều bệnh nặng nhất l&agrave; rối loạn tiền đ&igrave;nh - PV). &ldquo;Bố mất sớm, một m&igrave;nh mẹ lo toan cho cuộc sống cho cả gia đ&igrave;nh. Em muốn nhắn gửi đến bố l&agrave; : Bố ơi con đ&atilde; đỗ được v&agrave;o trường đại học m&agrave; con y&ecirc;u th&iacute;ch, con hứa sẽ lo toan cho mẹ&rdquo;.&lt;/p&gt;&lt;p&gt;Hy vọng đ&acirc;y sẽ l&agrave; nguồn động vi&ecirc;n kh&iacute;ch lệ để em thực hiện ước mơ của m&igrave;nh tại Trường Đại học Thủy lợi. C&acirc;u n&oacute;i m&agrave; em nhắc lại rất nhiều lần trong c&acirc;u chuyện, đ&oacute; l&agrave; em lu&ocirc;n tự tin với quyết định chọn trường của bản th&acirc;n m&igrave;nh, em lu&ocirc;n tin đ&acirc;y&amp;nbsp;l&agrave; ng&ocirc;i trường m&agrave; em mơ ước được học tập.&lt;/p&gt;&lt;p&gt;Thực hiện: B&igrave;nh Dương, Nh&oacute;m truyền th&ocirc;ng CTSV&lt;/p&gt;&lt;p&gt;Nguồn: &lt;a href=&quot;http://www.tlu.edu.vn/tin-tuc/chang-trai-thu-khoa-chon-dh-thuy-loi-tu-13383&quot;&gt;http://www.tlu.edu.vn/tin-tuc/chang-trai-thu-khoa-chon-dh-thuy-loi-tu-13383&lt;/a&gt;&lt;/p&gt;', '', 1, 0, 1, 2, 39, NULL),
(88, 'Trường Đại học Thủy lợi trao trên 1 tỷ đồng học bổng cho sinh viên tại Lễ Khai giảng năm học mới 2020-2021', '2020-11-03 07:36:33', NULL, '&lt;p&gt;(TLU) - H&ograve;a trong kh&ocirc;ng kh&iacute; chuẩn bị kỉ niệm ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam 20/11, h&ocirc;m nay (2/11), Trường Đại học Thủy lợi long trọng tổ chức lễ khai giảng năm học mới v&agrave; ch&agrave;o đ&oacute;n hơn 4.000 t&acirc;n sinh vi&ecirc;n K62 đến học tập tại Trường.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/thang%2011/1A7A6448.JPG?ver=2020-11-02-145835-163&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Văn nghệ ch&agrave;o mừng&lt;/p&gt;&lt;p&gt;Tham dự v&agrave; ch&uacute;c mừng thầy tr&ograve; Nh&agrave; trường c&oacute; &Ocirc;ng L&ecirc; Quốc Doanh - Thứ trưởng Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n;&amp;nbsp;GS. TS Vũ Trọng Hồng &ndash; Nguy&ecirc;n Thứ trưởng Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n; TS. Ho&agrave;ng Văn Thắng - Nguy&ecirc;n Thứ trưởng Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n - Chủ tịch Hội Cựu sinh vi&ecirc;n Trường Đại học Thủy lợi. Về ph&iacute;a Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo c&oacute;&amp;nbsp;PGS.TS Nguyễn Thu Thủy - Vụ trưởng Vụ Gi&aacute;o dục đại học. B&agrave;&amp;nbsp;Nguyễn&amp;nbsp;Tố&amp;nbsp;Quy&ecirc;n&amp;nbsp;&ndash;Ph&oacute;&amp;nbsp;B&iacute; thư&amp;nbsp;Đảng ủy&amp;nbsp;Khối&amp;nbsp;c&aacute;c trường đại học, cao đẳng&amp;nbsp;H&agrave; Nội...Đại diện c&aacute;c đơn vị chức năng thuộc cơ quan Bộ v&agrave; c&aacute;c Cục, Vụ, Viện; Đại sứ qu&aacute;n đến từ c&aacute;c nước L&agrave;o, Campuchia v&agrave; đại biểu ch&iacute;nh quyền địa phương.&lt;/p&gt;&lt;p&gt;Đặc biệt, chung vui ng&agrave;y khai giảng c&ograve;n c&oacute; c&aacute;c đồng ch&iacute; nguy&ecirc;n l&agrave; l&atilde;nh đạo của trường qua c&aacute;c thời kỳ v&agrave; c&aacute;c nh&agrave; t&agrave;i trợ đ&atilde; nhiều năm gắn b&oacute; với trường.&lt;/p&gt;&lt;p&gt;Về ph&iacute;a Nh&agrave; trường c&oacute; NGND. GS.TS.Nguyễn Quang Kim &ndash; B&iacute; thư Đảng ủy &ndash; Chủ tịch Hội đồng trường; GS. TS Trịnh Minh Thụ - Hiệu trưởng Trường Đại học Thủy lợi, đại diện Đảng ủy, Ban Gi&aacute;m hiệu, c&aacute;c Ph&ograve;ng, Khoa, Ban, Viện trong v&agrave; ngo&agrave;i trường c&ugrave;ng đ&ocirc;ng đảo sinh vi&ecirc;n, học vi&ecirc;n đ&atilde; c&oacute; mặt tham dự.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/thang%2011/1A7A6458.JPG?ver=2020-11-02-152641-510&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Lễ ch&agrave;o cờ trang nghi&ecirc;m trong ng&agrave;y khai&amp;nbsp;giảng năm học mới 2020-2021&lt;/p&gt;&lt;p&gt;Mở đầu lễ khai giảng, GS.TS Nguyễn Trung Việt - Ph&oacute; hiệu trưởng Nh&agrave; trường&amp;nbsp;đọc Thư gửi to&agrave;n ng&agrave;nh gi&aacute;o dục nh&acirc;n dịp&amp;nbsp;Khai giảng năm học mới của Tổng B&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/thang%2011/1A7A6561.JPG?ver=2020-11-02-153011-347&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;GS.TS Nguyễn Trung Việt - Ph&oacute; hiệu trưởng Nh&agrave; trường đọc Thư của Chủ tịch nước&lt;/p&gt;&lt;p&gt;Trong b&agrave;i diễn văn khai giảng, GS.TS Trịnh Minh Thụ - Hiệu trưởng khẳng định, c&aacute;c gi&aacute; trị m&agrave; Nh&agrave; trường lu&ocirc;n hướng tới l&agrave; &lt;strong&gt;Đo&agrave;n kết &ndash; Ch&iacute;nh trực &ndash; T&ocirc;n trọng &ndash; Chất lượng v&agrave; Kh&aacute;t vọng&lt;/strong&gt;. Do đ&oacute;, Trường Đại học Thủy lợi&amp;nbsp;kh&ocirc;ng ngừng n&acirc;ng cao chất lượng dạy v&agrave; học; lấy người học l&agrave;m trung t&acirc;m; n&acirc;ng cao chất lượng c&ocirc;ng t&aacute;c quản l&yacute;, đ&agrave;o tạo v&agrave; nghi&ecirc;n cứu khoa học, hội nhập quốc tế.&lt;/p&gt;&lt;p&gt;Năm học 2019-2020 l&agrave; năm học kh&aacute; đặc biệt do bị ảnh hưởng dịch Covid 19, mọi hoạt động đều bị ảnh hưởng. Tuy nhi&ecirc;n, Nh&agrave; trường đ&atilde; c&oacute; những giải ph&aacute;p ph&ograve;ng, chống, th&iacute;ch ứng kịp thời với t&igrave;nh h&igrave;nh dịch bệnh để thực hiện thắng lợi nhiệm vụ v&agrave; kế hoạch đ&atilde; đề ra.&lt;/p&gt;&lt;p&gt;Thay mặt Đảng ủy, Ban gi&aacute;m hiệu v&agrave; Hội đồng trường, GS.TS Trịnh Minh Thụ đ&atilde; đ&aacute;nh hồi trống Khai trường, ch&iacute;nh thức tuy&ecirc;n bố Khai giảng năm học mới 2020-2021.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/thang%2011/1A7A6629.JPG?ver=2020-11-02-154747-940&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;GS. TS Trịnh Minh Thụ - Hiệu trưởng Nh&agrave; trường đ&aacute;nh trống khai giảng năm học mới&lt;/p&gt;&lt;p&gt;Ch&uacute;c mừng thầy v&agrave; tr&ograve; Trường Đại học Thủy lợi, Thứ trưởng Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n - &Ocirc;ng L&ecirc; Quốc Doanh ghi nhận v&agrave; đ&aacute;nh gi&aacute; cao&amp;nbsp;những đ&oacute;ng g&oacute;p to lớn của Nh&agrave; trường trong sự nghiệp đ&agrave;o tạo nguồn nh&acirc;n lực chất lượng cao phục vụ x&acirc;y dựng v&agrave; ph&aacute;t triển đất nước. Thứ trưởng tin tưởng, với truyền thống tốt đẹp, sự nỗ lực kh&ocirc;ng mệt mỏi, sự cống hiến tận tụy của thầy v&agrave; tr&ograve;, Trường Đại học Thủy lợi khẳng định sẽ ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ năm học 2020-2021.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/thang%2011/1A7A6693.JPG?ver=2020-11-02-155428-287&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Thứ trưởng Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n - &Ocirc;ng L&ecirc; Quốc Doanh ph&aacute;t biểu ch&uacute;c mừng&lt;/p&gt;&lt;p&gt;Cũng tại lễ khai giảng, Nh&agrave; trường đ&atilde; c&ocirc;ng bố c&aacute;c quyết định v&agrave; trao tr&ecirc;n 1 tỷ đồng học bổng cho sinh vi&ecirc;n kh&oacute;a 62. Trong đ&oacute;, trao cho 5 sinh vi&ecirc;n c&oacute; điểm tr&uacute;ng tuyển cao nhất v&agrave;o Trường học bổng to&agrave;n phần trị gi&aacute; 90 triệu đồng/ suất; trao học bổng b&aacute;n phần cho&amp;nbsp;15 sinh vi&ecirc;n c&oacute; điểm tr&uacute;ng tuyển cao nhất v&agrave;o c&aacute;c ng&agrave;nh truyền thống với gi&aacute; trị 45 triệu đồng/ suất v&agrave; học bổng của c&aacute;c doanh nghiệp d&agrave;nh cho sinh vi&ecirc;n c&oacute; điểm cao nhất v&agrave;o c&aacute;c ng&agrave;nh.&amp;nbsp;Tại đ&acirc;y, đại diện l&atilde;nh đạo trường cũng đ&oacute;n nhận v&agrave; tr&acirc;n trọng sự ủng hộ&amp;nbsp;qu&yacute; b&aacute;u của c&aacute;c nh&agrave; t&agrave;i trợ cho quỹ học bổng của Nh&agrave; trường nh&acirc;n năm học mới.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/thang%2011/1A7A6944.JPG?ver=2020-11-02-160053-300&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;GS. TS Trịnh Minh Thụ trao học bổng to&agrave;n phần cho&amp;nbsp;5 sinh vi&ecirc;n c&oacute; điểm tr&uacute;ng tuyển cao nhất v&agrave;o Trường&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/thang%2011/1A7A6808.JPG?ver=2020-11-02-160331-197&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/thang%2011/1A7A6796.JPG?ver=2020-11-02-160330-540&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Trường Đại học Thủy&amp;nbsp;lợi đ&oacute;n nhận sự ủng hộ qu&yacute; b&aacute;u của c&aacute;c nh&agrave; t&agrave;i trợ&lt;/p&gt;&lt;p&gt;Nh&acirc;n dịp khai giảng năm học mới, c&aacute;n bộ, giảng vi&ecirc;n Trường Đại học Thủy lợi cũng ủng hộ 100 triệu đồng hỗ trợ đồng b&agrave;o miền Trung chịu ảnh hưởng do thi&ecirc;n tai. Việc l&agrave;m n&agrave;y thể hiện tinh thần tương th&acirc;n tương &aacute;i, một truyền thống qu&yacute; b&aacute;u&amp;nbsp;của d&acirc;n tộc Việt Nam.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/thang%2011/1A7A6997.JPG?ver=2020-11-02-160918-630&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Trường Đại học Thủy lợi hướng về miền Trung&lt;/p&gt;&lt;p&gt;Năm học mới 2020-2021 đ&atilde; bắt đầu, xin k&iacute;nh ch&uacute;c c&aacute;c c&aacute;n bộ, giảng vi&ecirc;n v&agrave; sinh vi&ecirc;n to&agrave;n trường một&amp;nbsp;năm thắng lợi, đo&agrave;n kết v&agrave; th&agrave;nh c&ocirc;ng!&lt;/p&gt;&lt;p&gt;Nguồn: &lt;a href=&quot;http://www.tlu.edu.vn/tin-tuc/truong-dai-hoc-thuy-loi-trao-tren-1-ty-13382&quot;&gt;http://www.tlu.edu.vn/tin-tuc/truong-dai-hoc-thuy-loi-trao-tren-1-ty-13382&lt;/a&gt;&lt;/p&gt;', 'badge badge-success Share', 0, 0, 1, 2, 39, NULL),
(89, 'Hội thảo khởi động Dự án “Quản lý tổng hợp bùn cát và lũ lụt ở các lưu vực sông hướng tới phát triển bền vững\"', '2020-11-03 07:37:29', NULL, '&lt;p&gt;Ng&agrave;y 22 th&aacute;ng 10 năm 2020, Hội thảo khởi động Dự &aacute;n &quot;Quản l&yacute; tổng hợp b&ugrave;n c&aacute;t v&agrave; lũ lụt ở c&aacute;c lưu vực s&ocirc;ng hướng tới ph&aacute;t triển bền vững&quot; giai đoạn 2020-2022 đ&atilde; được tổ chức trực tuyến.&lt;/p&gt;&lt;p&gt;Ng&agrave;y 22 th&aacute;ng 10 năm 2020, Hội thảo khởi động Dự &aacute;n &quot;Quản l&yacute; tổng hợp b&ugrave;n c&aacute;t v&agrave; lũ lụt ở c&aacute;c lưu vực s&ocirc;ng hướng tới ph&aacute;t triển bền vững&quot; &amp;nbsp;giai đoạn 2020-2022 đ&atilde; được tổ chức trực tuyến. Tham gia buổi lễ c&oacute; chuy&ecirc;n gia của dự &aacute;n đến từ: Trường Đại học Thủy lợi, Việt Nam; Trường Đại học Kyoto, Nhật Bản; Trường Đại học bang Isabela, Phi-l&iacute;p-pin v&agrave; một số cơ quan nghi&ecirc;n cứu kh&aacute;c. Dự &aacute;n được t&agrave;i trợ bởi Mạng lưới Nghi&ecirc;n cứu Thay đổi To&agrave;n cầu Ch&acirc;u &Aacute;- Th&aacute;i B&igrave;nh Dương (APN-GCR), Nhật Bản.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/HTQT/2020/2020-10-22%20Du%20an%20APN%201.jpg?ver=2020-10-30-165821-327&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;i&gt;Hội thảo khởi động dự &aacute;n tại TLU&lt;/i&gt;&lt;/p&gt;&lt;p&gt;Biến đổi kh&iacute; hậu g&acirc;y ra những mối hiểm họa lớn từ lũ lụt v&agrave; hạn h&aacute;n đối với c&aacute;c quốc gia tr&ecirc;n thế giới, trong đ&oacute; Nhật Bản, Việt Nam v&agrave; Phi-l&iacute;p-pin l&agrave; ba trong số những quốc gia bị ảnh hưởng nặng nề nhất. Những can thiệp của con người như ph&aacute; rừng, x&acirc;y dựng đập v&agrave; mở rộng hệ thống thủy lợi đ&atilde; l&agrave;m gia tăng t&aacute;c động của biến đổi kh&iacute; hậu.&lt;/p&gt;&lt;p&gt;Dự &aacute;n được đề xuất nhằm đ&aacute;nh gi&aacute; t&aacute;c động của biến đổi kh&iacute; hậu v&agrave; c&aacute;c can thiệp của con người đối với việc bồi lắng hồ chứa, ngập lụt, c&aacute;c hoạt động n&ocirc;ng nghiệp v&agrave; x&oacute;i lở bờ s&ocirc;ng v&agrave; bờ biển tr&ecirc;n lưu vực s&ocirc;ng Vũ Gia-Thu Bồn ở Việt Nam v&agrave; lưu vực s&ocirc;ng Cagayan ở Phi-l&iacute;p -pin. Tr&ecirc;n cơ sở đ&oacute; c&aacute;c kế hoạch quản l&yacute; tổng hợp b&ugrave;n c&aacute;t v&agrave; lũ lụt sẽ được thiết lập để hướng tới mục ti&ecirc;u ph&aacute;t triển bền vững. Dự &aacute;n sẽ tổ chức một số kh&oacute;a đ&agrave;o tạo, hội thảo để đ&agrave;o tạo c&aacute;c nh&agrave; nghi&ecirc;n cứu trẻ, c&aacute;c b&ecirc;n li&ecirc;n quan, c&aacute;c nh&agrave; hoạch định ch&iacute;nh s&aacute;ch, cộng đồng địa phương v&agrave; mở rộng mạng lưới hợp t&aacute;c giữa c&aacute;c nước Ch&acirc;u &Aacute; v&agrave; c&aacute;c chương tr&igrave;nh ph&aacute;t triển to&agrave;n cầu.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://www.tlu.edu.vn/Portals/0/HTQT/2020/2020-10-22%20Du%20an%20APN%202.png?ver=2020-10-30-165531-950&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;i&gt;C&aacute;c hợp phần nghi&ecirc;n cứu của Dự &aacute;n&lt;/i&gt;&lt;/p&gt;&lt;p&gt;Với c&aacute;c nội dung tr&ecirc;n, Dự &aacute;n dự kiến sẽ c&oacute; những đ&oacute;ng g&oacute;p hiệu quả trong việc cải thiện quản l&yacute; lưu vực s&ocirc;ng ở Việt Nam v&agrave; tăng cường hợp t&aacute;c giữa ĐHTL, Viện Nghi&ecirc;n cứu ph&ograve;ng chống thi&ecirc;n tai - Đại học Kyoto v&agrave; c&aacute;c đối t&aacute;c ASEAN.&lt;/p&gt;&lt;p&gt;Nguồn: &lt;a href=&quot;http://www.tlu.edu.vn/tin-tuc/hoi-thao-khoi-dong-du-an-quan-ly-tong-13377&quot;&gt;http://www.tlu.edu.vn/tin-tuc/hoi-thao-khoi-dong-du-an-quan-ly-tong-13377&lt;/a&gt;&lt;/p&gt;', 'badge badge-success Share', 11, 0, 1, 2, 39, NULL),
(90, 'Hội thảo \"Sử dụng mạng xã hội an toàn - góc nhìn từ tiêu chuẩn cộng đồng facebook\"', '2020-11-03 07:38:54', NULL, '&lt;p&gt;Những năm gần đ&acirc;y, mạng x&atilde; hội (MXH) đ&atilde; c&oacute; bước ph&aacute;t triển mạnh mẽ, t&aacute;c động lớn đến đời sống x&atilde; hội ở hầu hết c&aacute;c quốc gia tr&ecirc;n thế giới, trong đ&oacute; c&oacute; Việt Nam.&amp;nbsp;MXH đ&atilde; trở th&agrave;nh một thuật ngữ phổ biến, kh&ocirc;ng c&ograve;n xa lạ với tất cả ch&uacute;ng ta v&agrave; đặc biệt đối với c&aacute;c bạn trẻ.&lt;/p&gt;&lt;p&gt;Theo con số thống k&ecirc; th&igrave; hơn 1/3 d&acirc;n số Việt Nam c&oacute; t&agrave;i khoản Facebook, v&agrave; gần 40% thanh thi&ecirc;́u ni&ecirc;n d&agrave;nh thời gian cho MXH từ 1-3 tiếng mỗi ng&agrave;y.&amp;nbsp;Tuy nhi&ecirc;n việc sử dụng MXH như thế n&agrave;o cho an to&agrave;n v&agrave; l&agrave;nh mạnh th&igrave; kh&ocirc;ng phải tất cả ch&uacute;ng ta đều nắm r&otilde;.&amp;nbsp;&lt;/p&gt;&lt;p&gt;Ch&iacute;nh v&igrave; vậy, ng&agrave;y 09/05/2019, khoa CNTT trường Đại học Thuỷ Lợi phối hợp với Hiệp hội Internet Việt Nam v&agrave; tập đo&agrave;n Facebook&amp;nbsp;tổ chức chương tr&igrave;nh hội thảo &ldquo;SỬ DỤNG MẠNG X&Atilde; HỘI AN TO&Agrave;N - G&Oacute;C NH&Igrave;N TỪ TI&Ecirc;U CHUẨN CỘNG ĐỒNG FACEBOOK&rdquo; .&lt;/p&gt;&lt;p&gt;Tham dự chương tr&igrave;nh, về ph&iacute;a khoa CNTT c&oacute; TS Nguyễn Thanh T&ugrave;ng - Trưởng khoa CNTT, về ph&iacute;a hiệp hội Internet c&oacute; &ocirc;ng Vũ Thế B&igrave;nh - Tổng thư k&yacute;, Ph&oacute; chủ tịch Hiệp hội Internet Việt Nam, b&agrave; Nguyễn &Aacute;nh Nguyệt - Gi&aacute;m đốc ch&iacute;nh s&aacute;ch c&ocirc;ng của FB tại VN, chuy&ecirc;n gia Snow White Smelser - Phụ tr&aacute;ch ch&iacute;nh s&aacute;ch v&agrave; c&aacute;c chương tr&igrave;nh về an to&agrave;n tr&ecirc;n FB của khu vực Ch&acirc;u &Aacute; Th&aacute;i B&igrave;nh Dương c&ugrave;ng c&aacute;c thầy c&ocirc; gi&aacute;o v&agrave; c&aacute;c bạn sinh vi&ecirc;n khoa CNTT.&amp;nbsp;&lt;/p&gt;&lt;p&gt;Chương tr&igrave;nh được d&agrave;nh cho c&aacute;c bạn sinh vi&ecirc;n, nhằm trang bị cho sv những kiến thức v&agrave; kĩ năng bổ &iacute;ch để sử dụng MXH một c&aacute;ch an to&agrave;n.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/facebookanh1.png&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;TS &ndash; Nguy&ecirc;̃n Thanh Tùng &ndash; Trưởng khoa CNTT &ndash; trường Đại học Thủy Lợi.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/facebookanh2.png&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;&Ocirc;ng Vũ Thế B&igrave;nh - Tổng thư k&yacute;, Ph&oacute; chủ tịch Hiệp hội Internet Việt Nam.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/facebookanh3_1.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Chuy&ecirc;n gia Snow White Smelser - Phụ tr&aacute;ch ch&iacute;nh s&aacute;ch v&agrave; c&aacute;c chương tr&igrave;nh về an to&agrave;n tr&ecirc;n FB của khu vực Ch&acirc;u &Aacute; Th&aacute;i B&igrave;nh Dương.&lt;/p&gt;&lt;p&gt;Nguồn: &lt;a href=&quot;http://cse.tlu.edu.vn/tin-tuc-thong-bao/hoi-thao-su-dung-mang-xa-hoi-an-toan--goc-nhin-253&quot;&gt;http://cse.tlu.edu.vn/tin-tuc-thong-bao/hoi-thao-su-dung-mang-xa-hoi-an-toan--goc-nhin-253&lt;/a&gt;&lt;/p&gt;', 'badge badge-success Share', 0, 0, 1, 2, 40, NULL),
(91, 'Kết quả nghiên cứu khoa học sinh viên lần thứ 32', '2020-11-03 07:39:52', NULL, '&lt;p&gt;Hội nghị khoa học thường ni&ecirc;n của sinh vi&ecirc;n khoa C&ocirc;ng nghệ th&ocirc;ng tin được tổ chức v&agrave;o ng&agrave;y &amp;nbsp;22/3/2019 tại khu hội thảo Quốc tế K1 Trường Đại học Thủy lợi. Đ&acirc;y l&agrave; hoạt động thường ni&ecirc;n được khoa CNTT tổ chức định k&igrave; h&agrave;ng năm. Vượt qua gần 20 đề t&agrave;i, năm nay 11 đề t&agrave;i được chọn b&aacute;o c&aacute;o trong ng&agrave;y hội NCKH, được chia l&agrave;m hai tiểu ban.&lt;/p&gt;&lt;p&gt;Năm nay, Hội nghị thu h&uacute;t 11 đề t&agrave;i nghi&ecirc;n cứu của sinh vi&ecirc;n v&agrave; được chia l&agrave;m hai tiểu ban. C&aacute;c sinh vi&ecirc;n tham gia nghi&ecirc;n cứu rải đều từ sinh vi&ecirc;n năm thứ nhất (K60) đến sinh vi&ecirc;n năm thứ 4 (K57). Điểm mới trong năm nay l&agrave; nhiều c&aacute;c sinh vi&ecirc;n năm nhất v&agrave; năm hai tham gia.&lt;/p&gt;&lt;p&gt;Hội nghị đ&atilde; nghe c&aacute;c nh&oacute;m đề t&agrave;i tr&igrave;nh b&agrave;y về c&aacute;c nội dung của đề t&agrave;i nghi&ecirc;n cứu trước hội đồng l&agrave; c&aacute;c nh&agrave; khoa học, c&aacute;c chuy&ecirc;n gia trong từng lĩnh vực nghi&ecirc;n cứu, c&ugrave;ng c&aacute;c giảng vi&ecirc;n, sinh vi&ecirc;n quan t&acirc;m đến phong tr&agrave;o nghi&ecirc;n cứu khoa học tham dự.&lt;/p&gt;&lt;p&gt;Kết quả:&lt;/p&gt;&lt;p&gt;02 Giải Nhất&lt;/p&gt;&lt;figure class=&quot;table&quot;&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;strong&gt;STT&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Nh&oacute;m sinh vi&ecirc;n&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Lớp&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Đề t&agrave;i&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Giảng vi&ecirc;n hướng dẫn&lt;/strong&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;1&lt;/td&gt;&lt;td&gt;Dương Văn Phụng&lt;/td&gt;&lt;td&gt;58PM&lt;/td&gt;&lt;td&gt;Nghi&ecirc;n cứu trợ l&yacute; ảo tiếng Việt tr&ecirc;n điện thoại android&lt;/td&gt;&lt;td&gt;ThS. Trương Xu&acirc;n Nam&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td rowspan=&quot;4&quot;&gt;2&lt;/td&gt;&lt;td&gt;Từ Việt Thảo&lt;/td&gt;&lt;td&gt;60HT&lt;/td&gt;&lt;td rowspan=&quot;4&quot;&gt;Nghi&ecirc;n cứu x&acirc;y dựng chatbox hỗ trợ sinh vi&ecirc;n trường Đại học Thủy Lợi&lt;/td&gt;&lt;td rowspan=&quot;4&quot;&gt;TS Đặng Thị Thu Hiền&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Đ&agrave;o Quang Minh&lt;/td&gt;&lt;td&gt;58HT&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Vũ Mạnh Dũng&lt;/td&gt;&lt;td&gt;59PM2&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Đ&agrave;o Minh Đức&lt;/td&gt;&lt;td&gt;60HT&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/figure&gt;&lt;p&gt;02 Giải Nh&igrave;&lt;/p&gt;&lt;figure class=&quot;table&quot;&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;strong&gt;STT&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Nh&oacute;m sinh vi&ecirc;n&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Lớp&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Đề t&agrave;i&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Giảng vi&ecirc;n hướng dẫn&lt;/strong&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td rowspan=&quot;3&quot;&gt;&lt;strong&gt;1&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;Nguyễn Ngọc Hiếu&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;&lt;strong&gt;57TH2&lt;/strong&gt;&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;X&acirc;y dựng trợ l&yacute; ảo hỗ trợ t&igrave;m kiếm th&ocirc;ng tin giảng vi&ecirc;n Đại học Thuỷ Lợi&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;TS Đỗ Văn Hải&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Nguyễn.T. Minh Hải&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Đặng Văn Việt&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td rowspan=&quot;3&quot;&gt;&lt;strong&gt;2&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;Trần T. Thảo Nhi&lt;/td&gt;&lt;td&gt;57TH1&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;&lt;p&gt;Giấu tin trong ảnh dựa tr&ecirc;n&lt;/p&gt;&lt;p&gt;phương ph&aacute;p&amp;nbsp; SIERPINSKI GASKET FRACTAL v&agrave; PLSB&lt;/p&gt;&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;Th.s Đinh Ph&uacute; H&ugrave;ng&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Nguyễn Trung Hiếu&lt;/td&gt;&lt;td&gt;58Th2&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Vũ Thị Như Quỳnh&lt;/td&gt;&lt;td&gt;57Th2&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/figure&gt;&lt;p&gt;02 Giải C&ocirc;ng nghệ&lt;/p&gt;&lt;figure class=&quot;table&quot;&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;strong&gt;STT&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Nh&oacute;m sinh vi&ecirc;n&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Lớp&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Đề t&agrave;i&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Giảng vi&ecirc;n hướng dẫn&lt;/strong&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td rowspan=&quot;3&quot;&gt;&lt;strong&gt;1&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;Trần Quang Linh&lt;/td&gt;&lt;td&gt;59HT&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;Chữ k&iacute; số v&agrave; ứng dụng rong h&oacute;a đơn diện tử&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;ThS Phạm Thanh B&igrave;nh&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Nguyễn.Q. T&ugrave;ng L&acirc;m&lt;/td&gt;&lt;td&gt;59TH1&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Từ Việt Thảo&lt;/td&gt;&lt;td&gt;60HT&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td rowspan=&quot;3&quot;&gt;&lt;strong&gt;2&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;L&ecirc; Trung Hậu&lt;/td&gt;&lt;td&gt;57TH4&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;ứng dụng Blockchain quản l&yacute; hợp đồng giao dịch&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;Th.s Đỗ Oanh Cường&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Ng&ocirc; Do&atilde;n Đạt&lt;/td&gt;&lt;td&gt;59HT&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Nguyễn Tiến Đạt&lt;/td&gt;&lt;td&gt;57Th2&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/figure&gt;&lt;p&gt;02 giải tiềm năng&lt;/p&gt;&lt;figure class=&quot;table&quot;&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;strong&gt;STT&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Nh&oacute;m sinh vi&ecirc;n&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Lớp&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Đề t&agrave;i&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Giảng vi&ecirc;n hướng dẫn&lt;/strong&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td rowspan=&quot;2&quot;&gt;1&lt;/td&gt;&lt;td&gt;Tống Đức Thắng&lt;/td&gt;&lt;td&gt;58TH2&lt;/td&gt;&lt;td rowspan=&quot;2&quot;&gt;Ứng dụng m&ocirc; h&igrave;nh nơ ron nh&acirc;n chập để g&aacute;n cho c&aacute;c đối&lt;/td&gt;&lt;td rowspan=&quot;2&quot;&gt;ThS Đinh Ph&uacute; H&ugrave;ng&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;B&ugrave;i Quang Minh&lt;/td&gt;&lt;td&gt;57TH1&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td rowspan=&quot;2&quot;&gt;2&lt;/td&gt;&lt;td&gt;Lưu Kh&aacute;nh Chi&lt;/td&gt;&lt;td&gt;58Th1&lt;/td&gt;&lt;td rowspan=&quot;2&quot;&gt;T&igrave;m hiểu về học tăng cường v&agrave; ứng dụng&lt;/td&gt;&lt;td rowspan=&quot;2&quot;&gt;ThS Trương Xu&acirc;n Nam&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Tưởng Thị Hồng Nhung&lt;/td&gt;&lt;td&gt;58HT&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/figure&gt;&lt;p&gt;&amp;nbsp;03 giải Khuyến kh&iacute;ch&lt;/p&gt;&lt;figure class=&quot;table&quot;&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;strong&gt;STT&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Nh&oacute;m sinh vi&ecirc;n&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Lớp&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Đề t&agrave;i&lt;/strong&gt;&lt;/td&gt;&lt;td&gt;&lt;strong&gt;Giảng vi&ecirc;n hướng dẫn&lt;/strong&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td rowspan=&quot;3&quot;&gt;1&lt;/td&gt;&lt;td&gt;Trương Minh Đức&lt;/td&gt;&lt;td&gt;58HT&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;Thiết kế game tr&ecirc;n CocosCreator v&agrave; SmartFoxServer&lt;/td&gt;&lt;td rowspan=&quot;3&quot;&gt;ThS Nguyễn Văn Nam&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Nguyễn Văn T&acirc;n&lt;/td&gt;&lt;td&gt;58TH1&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Nguyễn Trung Hiếu&lt;/td&gt;&lt;td&gt;58TH2&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td rowspan=&quot;2&quot;&gt;2&lt;/td&gt;&lt;td&gt;Ng&ocirc; Xu&acirc;n Tr&agrave;&lt;/td&gt;&lt;td&gt;60PM1&lt;/td&gt;&lt;td rowspan=&quot;2&quot;&gt;Tăng cường chất lượng ảnh tối&lt;/td&gt;&lt;td rowspan=&quot;2&quot;&gt;ThS Đinh Ph&uacute; H&ugrave;ng&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Nguyễn Đ&igrave;nh Khoa&lt;/td&gt;&lt;td&gt;58PM&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td rowspan=&quot;4&quot;&gt;3&lt;/td&gt;&lt;td&gt;Nguyễn Danh Dũng&lt;/td&gt;&lt;td&gt;58TH3&lt;/td&gt;&lt;td rowspan=&quot;4&quot;&gt;Nghi&ecirc;n cứu m&ocirc; h&igrave;nh giải thuật v&agrave; triển khai hệ thống Private Cloud cho doanh nghiệp&lt;/td&gt;&lt;td rowspan=&quot;4&quot;&gt;ThS Đỗ Oanh Cường&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Nguyễn Ngọc M Ch&acirc;u&lt;/td&gt;&lt;td&gt;60HT&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Nguyễn Văn L&yacute;&lt;/td&gt;&lt;td&gt;58Th3&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;L&ecirc; Xu&acirc;n Trường&lt;/td&gt;&lt;td&gt;58Th3&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/figure&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;Theo đ&aacute;nh gi&aacute; của Hội đồng, chất lượng c&aacute;c đề t&agrave;i năm nay nh&igrave;n chung l&agrave; tốt, c&oacute; sự đổi mới về chiều s&acirc;u trong nghi&ecirc;n cứu v&agrave; khả năng ứng dụng. Hội đồng đ&aacute;nh gi&aacute; cao chất lượng b&aacute;o c&aacute;o, cũng như h&agrave;m lượng khoa học, tiến độ của 11 đề t&agrave;i. Ban tổ chức hy vọng những lần tổ chức tới đ&acirc;y, số lượng đề t&agrave;i sẽ ng&agrave;y c&agrave;ng tăng về số lượng v&agrave; chất lượng b&agrave;i b&aacute;o c&aacute;o cũng như số lượng sinh vi&ecirc;n nghi&ecirc;n cứu.&lt;/p&gt;&lt;p&gt;Một v&agrave;i h&igrave;nh ảnh trong hội nghị&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/anh1_1.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;TS Nguyễn Thanh T&ugrave;ng, Trưởng khoa CNTT c&ugrave;ng c&aacute;c thầy c&ocirc; trong Hội nghị&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/anh2_1.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Bạn Dương Văn Phụng lớp 58PM đạt giải Nhất với đề t&agrave;i &ldquo; Nghi&ecirc;n cứu trợ l&yacute; ảo tiếng Việt tr&ecirc;n điện thoại android do ThS Trương Xu&acirc;n Nam hướng dẫn.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/anh3_1.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Tiểu ban C&ocirc;ng nghệ th&ocirc;ng tin 1.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/anh4_1.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Tiểu ban: C&ocirc;ng nghệ th&ocirc;ng tin 2.&lt;/p&gt;&lt;p&gt;Nguồn: &lt;a href=&quot;http://cse.tlu.edu.vn/tin-tuc-thong-bao/ket-qua-nghien-cuu-khoa-hoc-sinh-vien-lan-thu-32-246&quot;&gt;http://cse.tlu.edu.vn/tin-tuc-thong-bao/ket-qua-nghien-cuu-khoa-hoc-sinh-vien-lan-thu-32-246&lt;/a&gt;&lt;/p&gt;', 'badge badge-success Share', 1, 0, 1, 2, 40, NULL),
(92, 'Công bố quyết định của Hiệu trưởng Bổ nhiệm viên chức quản lý cấp bộ môn khoa CNTT', '2020-11-03 07:40:45', NULL, '&lt;p&gt;Trong những ng&agrave;y đầu th&aacute;ng 1 năm 2019, thừa ủy quyền của Hiệu trưởng trường Đại học Thủy Lợi, Đảng ủy - Ban Chủ nhiệm khoa C&ocirc;ng nghệ th&ocirc;ng tin đ&atilde; tiến h&agrave;nh trao quyết định bổ nhiệm chức vụ Trưởng Bộ m&ocirc;n To&aacute;n học cho TS Nguyễn Hữu Thọ, Ph&oacute; trưởng Bộ m&ocirc;n To&aacute;n học cho TS Đỗ L&acirc;n, Ph&oacute; trưởng Bộ m&ocirc;n Hệ thống th&ocirc;ng tin đối với TS Trần Mạnh Tuấn, Khoa C&ocirc;ng nghệ th&ocirc;ng tin.&lt;/p&gt;&lt;p&gt;Lễ c&ocirc;ng bố quyết định được tổ chức trang trọng tại ph&ograve;ng 301 nh&agrave; C1 với sự tham dự của TS Nguyễn Thanh T&ugrave;ng &ndash; Trưởng khoa; Đảng Ủy, Ban chủ nhiệm Khoa, c&aacute;c Bộ m&ocirc;n v&agrave; c&aacute;c c&aacute;n bộ được bổ nhiệm.&lt;/p&gt;&lt;p&gt;Ph&aacute;t biểu giao nhiệm vụ, TS Nguyễn Thanh T&ugrave;ng &ndash; Trưởng khoa thay mặt Đảng ủy, Ban Chủ nhiệm khoa đ&atilde; cảm ơn v&agrave; ch&uacute;c mừng c&aacute;c đồng ch&iacute; được bổ nhiệm dịp n&agrave;y, đ&atilde; lu&ocirc;n nỗ lực đ&oacute;ng g&oacute;p cho sự ph&aacute;t triển của Bộ m&ocirc;n v&agrave; Khoa trong suốt thời gian qua.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/anh%20Tho_1.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Trao quyết định bổ nhiệm cho TS Nguyễn Hữu Thọ&lt;/p&gt;&lt;p&gt;Đối với Bộ m&ocirc;n To&aacute;n l&agrave; một Bộ m&ocirc;n lớn nhất Khoa, với vị tr&iacute; Trưởng Bộ m&ocirc;n, Trưởng khoa mong muốn TS Nguyễn Hữu Thọ sẽ tiếp tục ph&aacute;t huy v&agrave; giữ được nề nếp của Bộ m&ocirc;n do Ths Phạm Xu&acirc;n Đồng g&acirc;y dựng trong những năm vừa qua. Hơn nữa Trưởng khoa cũng mong TS Nguyễn Hữu Thọ sẽ thực hiện tốt c&ocirc;ng t&aacute;c quản l&yacute;, thực hiện tốt chức năng, nhiệm vụ đơn vị.&lt;/p&gt;&lt;p&gt;Với TS Trần Mạnh Tuấn v&agrave; TS Đỗ L&acirc;n, Ban chủ nhiệm Khoa tin tưởng c&aacute;c đồng ch&iacute; sẽ tiếp tục ph&aacute;t huy năng lực, tham mưu cho l&atilde;nh đạo c&aacute;c Bộ m&ocirc;n, thực hiện tốt c&aacute;c nhiệm vụ được giao&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/anh%20Tuan.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Trao quyết định bổ nhiệm cho TS Trần Mạnh Tuấn&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/do%20lan_1.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;Trao quyết định bổ nhiệm cho TS Đỗ L&acirc;n&lt;/p&gt;&lt;p&gt;Đ&aacute;p lại sự tin tưởng của Đảng ủy, Ban chủ nhiệm Khoa, sự t&iacute;n nhiệm của c&aacute;c đồng nghiệp, c&aacute;c c&aacute;n bộ được bổ nhiệm đ&atilde; gửi lời c&aacute;m ơn v&agrave; thể hiện quyết t&acirc;m sẽ l&agrave;m tốt c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n v&agrave; ho&agrave;n th&agrave;nh tốt c&aacute;c nhiệm vụ được giao trong cương vị mới.&lt;/p&gt;&lt;p&gt;Bước sang năm mới 2019, hy vọng với sự quan t&acirc;m của Đảng ủy &ndash; Ban chủ nhiệm Khoa, Bộ m&ocirc;n To&aacute;n v&agrave; Bộ m&ocirc;n Hệ thống th&ocirc;ng tin sẽ tiếp tục ph&aacute;t huy những th&agrave;nh t&iacute;ch đ&atilde; đạt được, ho&agrave;n th&agrave;nh tốt c&aacute;c nhiệm vụ được giao tạo kh&iacute; thế ch&agrave;o mừng kỷ niệm 60 năm th&agrave;nh lập Trường v&agrave; 18 năm th&agrave;nh lập Khoa.&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/lan%20va%20BM.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;TS Đỗ L&acirc;n chụp ảnh kỷ niệm với c&aacute;c thầy c&ocirc; Bộ m&ocirc;n To&aacute;n&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/A%20Tho%20va%20BM.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;TS. Nguyễn Hưu Thọ chụp ảnh kỷ niệm với c&aacute;c thầy c&ocirc; Bộ m&ocirc;n To&aacute;n&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;http://cse.tlu.edu.vn/Portals/0/Anh%20Tuan%20va%20DN.jpg&quot; alt=&quot;&quot;&gt;&lt;/figure&gt;&lt;p&gt;TS. Trần Mạnh Tuấn chụp ảnh kỷ niệm với c&aacute;c đồng nghiệp&lt;/p&gt;&lt;p&gt;Nguồn: &lt;a href=&quot;http://cse.tlu.edu.vn/tin-tuc-thong-bao/cong-bo-quyet-dinh-cua-hieu-truong-bo-nhiem-vien-234&quot;&gt;http://cse.tlu.edu.vn/tin-tuc-thong-bao/cong-bo-quyet-dinh-cua-hieu-truong-bo-nhiem-vien-234&lt;/a&gt;&lt;/p&gt;', 'badge badge-success Share', 0, 0, 1, 2, 40, NULL),
(93, 'MMO4ME chia sẻ vượt bão miền Trung - Bão số 9 - 2020', '2020-11-03 07:41:39', NULL, '&lt;p&gt;Ch&agrave;o mọi người,&lt;br&gt;&lt;br&gt;Lũ vừa mới r&uacute;t chưa được bao l&acirc;u th&igrave; B&atilde;o đ&atilde; ập tới với đồng b&agrave;o miền Trung, khiến m&igrave;nh v&ocirc; c&ugrave;ng b&agrave;ng ho&agrave;ng, đau x&oacute;t. Cảm x&uacute;c của m&igrave;nh như muốn vỡ &ograve;a, khi thấy được bức ảnh dưới đ&acirc;y ( do 1 mem gửi trong group Telegram của nh&oacute;m &lt;strong&gt;MMO4ME &lt;/strong&gt;), B&atilde;o đợt n&agrave;y v&ocirc; c&ugrave;ng khủng khiếp, sức gi&oacute; l&ecirc;n tới 150km/h, đ&aacute;nh thẳng trực diện v&agrave;o khu vực Qu&atilde;ng Ng&atilde;i, Tam Kỳ v&agrave; Hội An. Topic được lập ra, mục đ&iacute;ch để anh em, bạn b&egrave; c&ugrave;ng theo d&otilde;i, đồng h&agrave;nh c&ugrave;ng miền Trung th&acirc;n thương.&lt;br&gt;&lt;br&gt;M&igrave;nh xin ch&uacute;c anh em miền Trung, đặc biệt l&agrave; những người đồng đội trong &lt;strong&gt;BQT MMO4ME&lt;/strong&gt; đang sinh sống tại miền Trung ki&ecirc;n cường vượt qua cơn &lt;strong&gt;bảo số 9&lt;/strong&gt;.&lt;br&gt;&lt;br&gt;&lt;strong&gt;H&atilde;y để lại&lt;/strong&gt; những lời ch&uacute;c b&igrave;nh an nhất cho những người ngo&agrave;i Miền Trung nha c&aacute;c bạn.&lt;br&gt;&lt;br&gt;Theo d&otilde;i &lt;strong&gt;Live Stream&lt;/strong&gt; &lt;strong&gt;b&atilde;o&lt;/strong&gt; tại đ&acirc;y: &lt;a href=&quot;https://www.ventusky.com/?p=15.21;108.89;7&amp;amp;l=gust&quot;&gt;https://www.ventusky.com/?p=15.21;108.89;7&amp;amp;l=gust&lt;/a&gt;&lt;br&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://mmo4me.com/attachments/1603869183291-png.160296/&quot; alt=&quot;1603869183291.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;strong&gt;MMO4ME&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;chia sẻ c&ugrave;ng miền Trung&lt;/p&gt;&lt;p&gt;&lt;strong&gt;BQT MMO4ME&lt;/strong&gt;&lt;/p&gt;', 'badge badge-success Share', 0, 0, 1, 2, 41, NULL);
INSERT INTO `posts` (`post_id`, `title`, `create_at`, `edit_at`, `body`, `tags`, `views`, `replys`, `status`, `user_id`, `topic_id`, `mitopic_id`) VALUES
(94, ' Giới thiệu dịch vụ của XM - sàn forex lớn hàng đầu Châu Âu', '2020-11-03 07:42:41', NULL, '&lt;p&gt;&lt;strong&gt;XM được th&agrave;nh lập v&agrave;o năm 2009, thuộc sở hữu của Trading Point Holdings &ndash; một c&ocirc;ng ty t&agrave;i ch&iacute;nh l&acirc;u đời tại Limassol, Cyprus. S&agrave;n đ&atilde; được cấp nhiều giấy ph&eacute;p uy t&iacute;n như ASIC, IFSC v&agrave; CySec; broker chỉ được cấp giấy ph&eacute;p n&agrave;y khi tu&acirc;n thủ những điều kiện nghi&ecirc;m ngặt đảm bảo quyền lợi của trader, v&igrave; vậy bạn c&oacute; thể y&ecirc;n t&acirc;m nạp tiền v&agrave;o giao dịch.&lt;/strong&gt;&lt;br&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://vnrebates.net/wp-content/uploads/2019/11/ZS0A7194.jpg&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;br&gt;​&lt;/p&gt;&lt;p&gt;2019 đ&aacute;nh dấu tr&ograve;n 10 năm hoạt động của XM với những con số ấn tượng: 2,500,000 kh&aacute;ch h&agrave;ng trader đến từ gần 200 quốc gia, hỗ trợ kh&aacute;ch h&agrave;ng 24/5 bằng hơn 30 ng&ocirc;n ngữ. Hiện tại, XM l&agrave; s&agrave;n forex đứng đầu c&aacute;c thị trường Trung Quốc, Nhật Bản, Th&aacute;i Lan, Malaysia&hellip;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1. Giao Dịch&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;XM đưa ra nhiều lựa chọn, để trader c&oacute; thể giao dịch với XM d&ugrave; kinh nghiệm v&agrave; quy m&ocirc; đầu tư của họ ở mức độ n&agrave;o. 4 loại t&agrave;i khoản&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Micro, Standard, Ultra Low&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;v&agrave;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Shares&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;ph&ugrave; hợp với nhu cầu của cả những người mới bắt đầu v&agrave; những trader l&acirc;u năm với c&aacute;c điều kiện linh hoạt, tiền nạp tối thiểu thấp v&agrave; đ&ograve;n bẩy l&ecirc;n tới&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;888:1&lt;/strong&gt;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://vnrebates.net/wp-content/uploads/2019/11/xm-forex.jpg&quot;&gt;&lt;/figure&gt;&lt;p&gt;Nếu Spread thấp l&agrave; ưu ti&ecirc;n lớn nhất khi chọn s&agrave;n của bạn, t&agrave;i khoản Ultra Low của XM c&oacute; tầm spread thấp nhất tr&ecirc;n thị trường (chỉ từ 0.6 pip). Với rất nhiều khoản ph&iacute; được miễn (kh&ocirc;ng ph&iacute; ẩn, phụ ph&iacute;, ph&iacute; nạp r&uacute;t, ph&iacute; đặt lệnh, ph&iacute; commission&hellip;), trader bớt đi những con số cần phải c&acirc;n nhắc m&agrave; chỉ cần tập trung nh&igrave;n v&agrave;o spread v&agrave; swap (ph&iacute; qua đ&ecirc;m) khi giao dịch.&lt;/p&gt;&lt;p&gt;Đặc biệt cho người mới, XM tặng thưởng Bonus 30$ v&agrave; Thưởng ch&agrave;o mừng 15% cho lần nạp tiền đầu ti&ecirc;n, c&ugrave;ng với nhiều khuyến mại hấp dẫn kh&aacute;c.&lt;/p&gt;&lt;p&gt;Mỗi kh&aacute;ch h&agrave;ng c&oacute; thể sở hữu tối đa 8 t&agrave;i khoản giao dịch v&agrave; 1 t&agrave;i khoản Shares, giao dịch hơn 1000 sản phẩm, bao gồm cả Forex v&agrave; CFD (cổ phiếu, chỉ số chứng kho&aacute;n, kim loại qu&yacute;, năng lượng, h&agrave;ng h&oacute;a).&lt;/p&gt;&lt;p&gt;&lt;strong&gt;XM c&ograve;n được biết đến như một s&agrave;n thực thi lệnh tốt, tỷ lệ thực thi l&agrave; 100%, trong đ&oacute; 99.35% c&aacute;c lệnh giao dịch được thực hiện dưới 1 gi&acirc;y, kh&ocirc;ng b&aacute;o gi&aacute; lại v&agrave; kh&ocirc;ng từ chối lệnh.&lt;/strong&gt;&lt;/p&gt;&lt;blockquote&gt;&lt;p&gt;&ldquo;Spread thấp sẽ chỉ c&oacute; &yacute; nghĩa khi bạn c&oacute; thể giao dịch được tr&ecirc;n ch&uacute;ng.&rdquo; &ndash; Constantinos Cleanthous, S&aacute;ng lập XM Group&lt;/p&gt;&lt;/blockquote&gt;&lt;p&gt;&lt;strong&gt;2.Nạp/R&uacute;t v&agrave; Hỗ Trợ&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;XM mới ph&aacute;t triển dịch vụ tập trung cho trader Việt Nam trong năm 2019, nhưng hiện tại đ&atilde; gần như ho&agrave;n to&agrave;n Việt h&oacute;a được dịch vụ:&lt;/p&gt;&lt;p&gt;&ndash; Đội ngũ hỗ trợ bằng tiếng Việt qua Chat trực tuyến, email v&agrave; điện thoại 5 ng&agrave;y/tuần. Đặc biệt mỗi t&agrave;i khoản kh&aacute;ch h&agrave;ng lẻ hay đối t&aacute;c đều c&oacute; một nh&acirc;n vi&ecirc;n Quản l&yacute; t&agrave;i khoản phụ tr&aacute;ch hỗ trợ&lt;/p&gt;&lt;p&gt;&ndash; Nạp &ndash; r&uacute;t tiền nhanh ch&oacute;ng v&agrave; thuận tiện qua những cổng thanh to&aacute;n nội địa như: qua ng&acirc;n h&agrave;ng địa phương, online banking, v&iacute; Ng&acirc;n lượng, thẻ Debit/Credit&hellip; b&ecirc;n cạnh c&aacute;c phương thức như v&iacute; Skrill, Neteller&lt;/p&gt;&lt;p&gt;&ndash; Website &ndash; phần mềm giao dịch &ndash; ứng dụng (app) của XM đều đ&atilde; c&oacute; lựa chọn ng&ocirc;n ngữ tiếng Việt&lt;/p&gt;&lt;p&gt;&ndash; Đội ngũ giảng vi&ecirc;n người Việt kinh nghiệm &amp;amp; uy t&iacute;n cho những hoạt động đ&agrave;o tạo&lt;/p&gt;&lt;p&gt;Ngo&agrave;i ra, XM cũng c&oacute; đội ngũ đại diện tại Việt Nam lu&ocirc;n lu&ocirc;n sẵn s&agrave;ng gặp mặt, trao đổi với kh&aacute;ch h&agrave;ng hoặc đối t&aacute;c, thay v&igrave; qua điện thoại hay email.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3. Đ&agrave;o tạo&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;XM rất ch&uacute; trọng định hướng v&agrave; ph&aacute;t triển&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;đ&agrave;o tạo miễn ph&iacute;&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;cho to&agrave;n bộ kh&aacute;ch h&agrave;ng. D&ugrave; bạn mới t&igrave;m hiểu về s&agrave;n v&agrave; chưa nạp tiền hay giao dịch, chỉ cần đăng k&yacute; t&agrave;i khoản với XM, bạn c&oacute; thể bắt đầu học trade online v&agrave; offline qua nhiều h&igrave;nh thức đa dạng:&lt;/p&gt;&lt;p&gt;&ndash; Hội thảo&lt;/p&gt;&lt;p&gt;&ndash; Hội thảo trực tuyến&lt;/p&gt;&lt;p&gt;&ndash; Liveshow/Livestream Facebook&lt;/p&gt;&lt;p&gt;&ndash; Lớp học offline&lt;/p&gt;&lt;p&gt;Những lớp học của XM đều c&oacute; lịch tr&igrave;nh cụ thể v&agrave; thường xuy&ecirc;n, c&oacute; thể tham khảo tại đ&acirc;y:&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.xm.com/vn/forex-classes&quot;&gt;https://www.xm.com/vn/forex-classes&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4. Đ&aacute;nh gi&aacute; tổng quan&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;XM hiện tại l&agrave; một trong những s&agrave;n lớn v&agrave; uy t&iacute;n nhất tr&ecirc;n thị trường, nổi bật như:&lt;/p&gt;&lt;p&gt;&ndash; XM Ultra Low c&oacute; spread thấp nhất thị trường&lt;/p&gt;&lt;p&gt;&ndash; Nạp r&uacute;t nhanh gọn,đội ngũ hỗ trợ kh&aacute;ch h&agrave;ng Việt Nam tốt&lt;/p&gt;&lt;p&gt;&ndash; Nhiều chương tr&igrave;nh khuyến m&atilde;i, đ&agrave;o tạo, tri &acirc;n cho kh&aacute;ch h&agrave;ng Việt Nam&lt;/p&gt;&lt;p&gt;&ndash; Uy t&iacute;n l&acirc;u năm, được cấp nhiều giấy ph&eacute;p&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Bạn c&oacute; thể đăng k&yacute; t&agrave;i khoản để bắt đầu giao dịch với XM tại đ&acirc;y:&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.xm.com/register/account/real?lang=vn&quot;&gt;&lt;strong&gt;https://www.xm.com/register/account/real?lang=vn&lt;/strong&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Nguồn: &lt;/strong&gt;&lt;a href=&quot;https://mmo4me.com/threads/gioi-thieu-dich-vu-cua-xm-san-forex-lon-hang-dau-chau-au.403294/&quot;&gt;https://mmo4me.com/threads/gioi-thieu-dich-vu-cua-xm-san-forex-lon-hang-dau-chau-au.403294/&lt;/a&gt;&lt;/p&gt;', 'badge badge-success Share', 4, 0, 1, 2, 41, NULL),
(95, 'Thu thập dữ liệu tự động sử dụng NodeJS và Selenium', '2020-11-03 07:51:19', NULL, '&lt;p&gt;Trong b&agrave;i viết n&agrave;y m&igrave;nh sẽ hướng dẫn c&aacute;c bạn &lt;strong&gt;thu thập dữ liệu của một website bất kỳ&lt;/strong&gt;. Với việc tự động h&oacute;a c&aacute;c t&aacute;c vụ như tự động điều hướng, tự động điền v&agrave;o input hay click c&aacute;c button ... ch&uacute;ng ta sẽ thu thập được khối lượng dữ liệu khổng lồ cho c&aacute;c mục đ&iacute;ch kh&aacute;c nhau. C&aacute;c bạn chỉ cần treo m&aacute;y rồi đi ra ngo&agrave;i l&agrave;m cốc cafe, quay trở v&agrave;o l&agrave; sẽ c&oacute; h&agrave;ng ng&agrave;n records được lưu trữ trong nh&aacute;y mắt.&lt;br&gt;&lt;br&gt;Trước khi bắt đầu, c&aacute;c bạn cần chuẩn bị c&aacute;c c&ocirc;ng cụ, thư viện hay bản c&agrave;i đặt của c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh &amp;amp; CSDL sau:&lt;br&gt;- &lt;strong&gt;ChromeDriver &lt;/strong&gt;(&lt;a href=&quot;https://vn-z.vn/redirect?to=aHR0cHM6Ly9zaXRlcy5nb29nbGUuY29tL2EvY2hyb21pdW0ub3JnL2Nocm9tZWRyaXZlci8=&quot;&gt;https://sites.google.com/a/chromium.org/chromedriver/&lt;/a&gt;)&lt;br&gt;- &lt;strong&gt;NodeJS &lt;/strong&gt;(&lt;a href=&quot;https://vn-z.vn/redirect?to=aHR0cHM6Ly9ub2RlanMub3JnL2VuL2Rvd25sb2FkLw==&quot;&gt;https://nodejs.org/en/download/&lt;/a&gt;)&lt;br&gt;- &lt;strong&gt;MongoDB&lt;/strong&gt; (&lt;a href=&quot;https://vn-z.vn/redirect?to=aHR0cHM6Ly93d3cubW9uZ29kYi5jb20vZG93bmxvYWQtY2VudGVy&quot;&gt;https://www.mongodb.com/download-center&lt;/a&gt;)&lt;br&gt;- &lt;strong&gt;RoboMongo&lt;/strong&gt; (&lt;a href=&quot;https://vn-z.vn/redirect?to=aHR0cHM6Ly9yb2JvbW9uZ28ub3JnL2Rvd25sb2Fk&quot;&gt;https://robomongo.org/download&lt;/a&gt;)&lt;br&gt;&lt;br&gt;&lt;strong&gt;C&agrave;i đặt NodeJS&lt;/strong&gt;&lt;br&gt;&lt;br&gt;C&aacute;c bạn tải v&agrave; c&agrave;i đặt phiển bản NodeJS mới nhất, trong bản c&agrave;i đặt sẽ c&oacute; sẵn Node package Manager (NPM) l&agrave; một c&ocirc;ng cụ quản l&yacute;, c&agrave;i đặt c&aacute;c package cần thiết cho NodeJS&lt;br&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://pakota159.files.wordpress.com/2016/09/node-js-development-brillmindz.jpg&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;strong&gt;C&agrave;i đặt Selenium, MongoDB package v&agrave; một số package kh&aacute;c&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;C&aacute;c bạn tạo một thư mục chứa project, mở&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;command promt&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;v&agrave; gọi&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;npm&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;để install c&aacute;c package sau:&lt;/p&gt;&lt;ul&gt;&lt;li&gt;npm install selenium-webdriver&lt;/li&gt;&lt;li&gt;npm install mongodb&lt;/li&gt;&lt;li&gt;npm install colors&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;Sau đ&oacute; copy ChromeDriver vừa tải về v&agrave;o thư mục mới tạo l&agrave; bạn đ&atilde; ho&agrave;n tất c&aacute;c bước chuẩn bị ban đầu, giờ ch&uacute;ng ta bắt tay v&agrave;o code th&ocirc;i&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://vn-z.vn/styles/default/xenforo/smilies/nemgach/brick.png&quot; alt=&quot;{brick}&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;strong&gt;M&atilde; nguồn v&agrave; v&iacute; dụ mẫu&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;Site m&agrave; m&igrave;nh d&ugrave;ng để demo thu thập dữ liệu l&agrave; site:&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://vn-z.vn/redirect?to=aHR0cHM6Ly90YW1zdWRldi5jb20=&quot;&gt;https://tamsudev.com&lt;/a&gt;&lt;/p&gt;&lt;p&gt;. m&igrave;nh sẽ tiến h&agrave;nh thu thập danh s&aacute;ch tất cả c&ocirc;ng ty CNTT tr&ecirc;n site n&agrave;y.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;gt;&amp;gt; Require c&aacute;c Package cần thiết&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;M&atilde;:&lt;/p&gt;&lt;p&gt;var webdriver = require(\'selenium-webdriver\'),\r\n &amp;nbsp; &amp;nbsp;chrome = require(\'selenium-webdriver/chrome\'),\r\n &amp;nbsp; &amp;nbsp;By = webdriver.By,\r\n &amp;nbsp; &amp;nbsp;until = webdriver.until;\r\nvar MongoClient = require(\'mongodb\').MongoClient;\r\nvar ObjectId = require(\'mongodb\').ObjectID;\r\nvar mongoUrl = \'mongodb://localhost:27017/tamsudev\';\r\nvar colors = require(\'colors/safe\');&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;gt;&amp;gt; Thiết lập c&aacute;c options cần thiết để chạy Chrome cho nuột&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://vn-z.vn/styles/default/xenforo/smilies/nemgach/biggrin.gif&quot; alt=&quot;{biggrin}&quot;&gt;&lt;/figure&gt;&lt;p&gt;M&atilde;:&lt;/p&gt;&lt;p&gt;var options = new chrome.Options();\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; \r\noptions.setUserPreferences({\r\n &amp;nbsp; &amp;nbsp;\'profile.default_content_setting_values.notifications\' : 2,\r\n &amp;nbsp; &amp;nbsp;\'profile.managed_default_content_settings.images\' : 2,\r\n &amp;nbsp; &amp;nbsp;\'profile.managed_default_content_settings.media_stream\' : 2,\r\n &amp;nbsp; &amp;nbsp;\'profile.managed_default_content_settings.popups\' : 2,\r\n});\r\n\r\noptions.addArguments(\'--headless\');\r\n\r\nvar capabilities = options.toCapabilities();&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;gt;&amp;gt; Khởi tạo driver, redirect tới trang v&agrave; thực hiện c&aacute;c t&aacute;c vụ tự động với c&aacute;c DOM element tr&ecirc;n trang (&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;c&aacute;c thao t&aacute;c của Selenium với DOM element ho&agrave;n to&agrave;n tương tự với javascript&lt;/p&gt;&lt;p&gt;&lt;strong&gt;)&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;M&atilde;:&lt;/p&gt;&lt;p&gt;var driver = new webdriver.Builder()\r\n &amp;nbsp; &amp;nbsp;.withCapabilities(capabilities)\r\n &amp;nbsp; &amp;nbsp;.forBrowser(\'chrome\')\r\n &amp;nbsp; &amp;nbsp;.build();\r\nvar counter = 0;\r\n\r\ndriver.get(\'https://tamsudev.com\');\r\ncrawlCompanies();\r\n\r\nfunction crawlCompanies() {\r\n &amp;nbsp; &amp;nbsp;console.log(colors.red(\'&amp;gt;&amp;gt;&amp;gt; Callback...\'));\r\n &amp;nbsp; &amp;nbsp;driver.findElements(webdriver.By.className(\'detail\')).then(function(details) {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;details.forEach(function(detail) {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;detail.findElement(webdriver.By.tagName(\'h4\')).then(function(h4) {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;h4.getAttribute(&quot;innerText&quot;).then(function(name) {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;h4.findElement(webdriver.By.tagName(\'a\')).then(function(a){\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;a.getAttribute(&quot;href&quot;).then(function(url) {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;var slug = url.replace(\'https://tamsudev.com/\',\'\');\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;counter++;\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;console.log(counter + \'.\' + colors.green(name + &quot; (&quot; + slug + &quot;)&quot;));\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;driver.executeScript(\'arguments[0].parentElement.innerHTML=&quot;&quot;;\', detail);\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;MongoClient.connect(mongoUrl, function (err, db) {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;var collection = db.collection(\'company\');\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;collection.findOne({\'slug\':slug}).then(function(doc) {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;if(!doc) {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;collection.insertOne({\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;name: name,\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;slug: slug\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;});\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;}\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;}); &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;});\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;});\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;}); &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;});\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;});\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;});\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;driver.executeScript(&quot;document.getElementById(\'loadMoreBtn\').click()&quot;).then(function() {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;crawlCompanies();\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;});\r\n &amp;nbsp; &amp;nbsp;}, function (err) {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;driver.executeScript(&quot;document.getElementById(\'loadMoreBtn\').click()&quot;).then(function() {\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;crawlCompanies();\r\n &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;});\r\n &amp;nbsp; &amp;nbsp;});\r\n}&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;gt;&amp;gt; Đ&oacute;ng driver sau khi sử dụng&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;M&atilde;:&lt;/p&gt;&lt;p&gt;console.log(colors.red(\'&amp;gt;&amp;gt;&amp;gt; Finished...\'));\r\ndriver.quit();&lt;/p&gt;&lt;p&gt;C&aacute;c bạn tạo một file trong folder đ&atilde; tạo ở tr&ecirc;n lưu với t&ecirc;n&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;nbsp;tamsudev.js&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;v&agrave; copy tất cả c&aacute;c code b&ecirc;n tr&ecirc;n để test thử (mở&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;cmd&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;tại đường dẫn thư mục v&agrave; g&otilde; lệnh:&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;node tamsudev.js&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;gt;&amp;gt; Kết quả&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;Kết quả sau khi chạy thử v&iacute; dụ mẫu tr&ecirc;n :&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://i.imgur.com/1Uuj0XP.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;i&gt;Log xuất ra ở m&agrave;n h&igrave;nh console&lt;/i&gt;&lt;br&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://i.imgur.com/Lwsykp9.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;​&lt;/p&gt;&lt;p&gt;&lt;i&gt;Data được lưu trong CSDL MongoDB&lt;/i&gt;&lt;br&gt;​&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Nhắn nhủ&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;Ngo&agrave;i khả năng thu thập dữ liệu một c&aacute;ch nhanh ch&oacute;ng th&igrave; thư viện&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Selenium&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;n&agrave;y c&ograve;n c&oacute; thể gi&uacute;p c&aacute;c bạn thực hiện c&aacute;c t&aacute;c vụ kh&aacute;c như tự động nhập liệu, đăng nhập đăng xuất, tự động đăng b&agrave;i ... v&agrave; rất tiện lợi cho c&aacute;c bạn tester sử dụng cho&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;automation testing&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;Cảm ơn c&aacute;c bạn đ&atilde; đọc hết b&agrave;i viết n&agrave;y, nếu c&aacute;c bạn c&oacute; thắc mắc g&igrave; th&igrave; h&atilde;y để lại b&igrave;nh luận b&ecirc;n dưới b&agrave;i viết, m&igrave;nh sẽ cố gắng trả lời c&aacute;c bạn trong thời gian sớm nhất&amp;nbsp;&lt;/p&gt;&lt;p&gt;Nguồn: &lt;a href=&quot;https://vn-z.vn/threads/thu-thap-du-lieu-tu-dong-su-dung-nodejs-va-selenium.1876/&quot;&gt;https://vn-z.vn/threads/thu-thap-du-lieu-tu-dong-su-dung-nodejs-va-selenium.1876/&lt;/a&gt;&lt;/p&gt;', 'badge badge-info Tuturial', 0, 0, 1, 2, 44, NULL),
(96, 'Ký sự về cuộc tấn công vào Vn-Zoom', '2020-11-03 07:52:39', NULL, '&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://upanh.vn-z.vn/images/2020/09/01/ddos-attack-735x400.jpg&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;br&gt;​&lt;/p&gt;&lt;p&gt;Xin ch&agrave;o c&aacute;c bạn th&agrave;nh vi&ecirc;n diễn đ&agrave;n Vn-Zoom th&acirc;n mến .&lt;br&gt;&lt;br&gt;H&ocirc;m nay m&igrave;nh sẽ viết lại qu&aacute; tr&igrave;nh Vn-Zoom bị tấn c&ocirc;ng DDoS trong khoảng thời gian từ hơn 3 th&aacute;ng nay ( khoảng thời gian bị DDoS mạnh nhất). Trong b&agrave;i viết kh&ocirc;ng chia sẻ s&acirc;u về biện ph&aacute;p ph&ograve;ng thủ hoặc c&aacute;c vấn đề về kỹ thuật. B&agrave;i viết n&agrave;y chưa truy cứu v&agrave; t&igrave;m được kẻ &quot;thủ &aacute;c&quot;. Hy vọng b&agrave;i viết n&agrave;y sẽ gi&uacute;p c&aacute;c anh em c&oacute; th&ecirc;m những gi&acirc;y ph&uacute;t thư gi&atilde;n khi chống DDoS.&lt;br&gt;&lt;br&gt;&lt;strong&gt;I. C&aacute;c giai đoạn tấn c&ocirc;ng&lt;/strong&gt;&lt;br&gt;&lt;br&gt;Vn-Zoom được m&igrave;nh v&agrave; một số anh em cựu BQT từ Vn-Zoom.com g&acirc;y dựng lại sau khi đơn vị quản l&yacute; .com ngừng vận h&agrave;nh.&lt;br&gt;&lt;br&gt;- Năm 2018 Thời gian đầu Server Vn-Zoom đặt m&aacute;y chủ tại ViettelDC, l&uacute;c n&agrave;y t&ecirc;n miền của Vn-Zoom được trỏ trực tiếp đến m&aacute;y chủ . Ch&iacute;nh điều n&agrave;y khiến Vn-Zoom li&ecirc;n tục bị hacker scan ,Brute force .v.v.v v&agrave;o t&agrave;i khoản quản trị nhằm mục đ&iacute;ch xấu của hacker.&lt;br&gt;Theo c&aacute;c anh em trong nghề sysadmin th&igrave; gần như tất cả c&aacute;c m&aacute;y chủ của Việt Nam nếu kh&ocirc;ng qua Proxy đều bị scan v&agrave; brute force h&agrave;ng ng&agrave;y.&lt;br&gt;Đ&acirc;y l&agrave; h&igrave;nh thức qu&eacute;t IP gốc của m&aacute;y chủ nhằm mục đ&iacute;ch xấu sau n&agrave;y. M&igrave;nh thấy rất nhiều trang web của c&aacute;c đơn vị vừa v&agrave; nhỏ tại Việt Nam thường xuy&ecirc;n để lộ IP M&aacute;y chủ gốc.&lt;br&gt;Thời gian n&agrave;y lượt tấn c&ocirc;ng VN-Zoom kh&ocirc;ng nhiều v&agrave; thường bị tường lửa mặc địch của hệ thống chặn v&agrave; block.&lt;br&gt;&lt;br&gt;- Năm 2019 : Sau khi ch&iacute;nh thức chuyển sang t&ecirc;n miền Vn-Z.vn l&uacute;c n&agrave;y c&oacute; nhiều đợt tấn c&ocirc;ng lạ hơn.&lt;br&gt;&lt;br&gt;Tuy nhi&ecirc;n v&igrave; chủ quan v&agrave; bảo thủ với định kiến kh&ocirc;ng th&iacute;ch d&ugrave;ng CDN ( thực ra l&agrave; kh&ocirc;ng c&oacute; k&iacute;nh ph&iacute;) n&ecirc;n m&igrave;nh vẫn để t&ecirc;n miền trỏ thẳng về m&aacute;y chủ.&lt;br&gt;C&aacute;c đợt tấn c&ocirc;ng vẫn diễn ra ,nhưng chưa đến mức nghi&ecirc;m trọng, m&igrave;nh thường d&ugrave;ng m&aacute;y chủ kiểm tra c&aacute;c IP n&agrave;o c&oacute; lượt request lạ v&agrave; block bằng tường lửa mặc định được c&agrave;i theo server.&lt;br&gt;&lt;br&gt;- Năm 2020: Kẻ tấn c&ocirc;ng bắt đầu tấn c&ocirc;ng chuy&ecirc;n nghiệp hơn. sau khi nắm chắc được c&aacute;c lỗ hổng từ hệ thống m&aacute;y chủ Vn-Zoom. L&uacute;c n&agrave;y Server được chuyển sang cụm của VNPT&lt;br&gt;&lt;br&gt;L&uacute;c n&agrave;y gần như Vn-Zoom duy tr&igrave; to&agrave;n bộ hệ thống mặc định, t&ecirc;n miền vẫn trỏ trực tiếp v&agrave;o m&aacute;y chủ. Điều n&agrave;y khiến IP gốc của m&aacute;y chủ lộ ho&agrave;n to&agrave;n.&lt;br&gt;&lt;br&gt;Trong khoảng thời gian từ th&aacute;ng th&aacute;ng 2 - th&aacute;ng 8 năm 2020 kẻ xấu đang thăm d&ograve; v&agrave; thỉnh thoảng test tool , l&uacute;c n&agrave;y m&igrave;nh vẫn d&ugrave;ng phương ph&aacute;p thủ c&ocirc;ng. Từ m&aacute;y chủ, g&otilde; lệnh kiểm tra xem c&oacute; IP n&agrave;o c&oacute; nhiều lượt connect lạ tiến h&agrave;nh block IP đ&oacute;. C&agrave;i đặt c&aacute;c rules cơ bản cho firewall tự động chặn theo danh s&aacute;ch, ngăn chặn c&aacute;c h&igrave;nh thức brute force , scan ..v.v.v&lt;br&gt;&lt;br&gt;Tth&aacute;ng 9 kẻ xấu bắt đầu tấn c&ocirc;ng một c&aacute;ch chuy&ecirc;n nghiệp hơn. C&aacute;ch DDoS chuy&ecirc;n nghiệp hơn, lượt request dồn dập, đ&aacute;nh thẳng v&agrave;o m&aacute;y chủ l&agrave;m qu&aacute; tải CPU v&agrave; Băng th&ocirc;ng của hệ thống.&lt;br&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://upanh.vn-z.vn/images/2020/10/24/image_2020_09_02T11_46_23_824Z.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;br&gt;​&lt;/p&gt;&lt;p&gt;&lt;br&gt;L&uacute;c n&agrave;y đội kỹ thuật từ ph&iacute;a nh&agrave; cung cấp m&aacute;y chủ th&ocirc;ng b&aacute;o buộc phải ngắt kết nối m&aacute;y chủ VN-Zoom . Lượt request rất lớn chiếm to&agrave;n bộ băng th&ocirc;ng ,c&oacute; thể đ&aacute;nh sập c&aacute;c dải IP kh&aacute;c.&lt;br&gt;&lt;br&gt;H&agrave;ng chục triệu request từ mạng lưới botnet li&ecirc;n tục tấn c&ocirc;ng dồn dập, buộc đội kỹ thuật phải chặn ho&agrave;n to&agrave;n hai chiều hướng quốc tế.&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://upanh.vn-z.vn/images/2020/10/24/image_2020_09_03T07_15_39_557Z.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://upanh.vn-z.vn/images/2020/10/24/Screenshot_2020-10-15-vps-2020-10-07-06-56-TWhiteCPTitleitle.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;br&gt;Server lập tức bị qu&aacute; tải ngay sau khi mon men mở lại đường quốc tế&lt;br&gt;​&lt;/p&gt;&lt;p&gt;&lt;br&gt;Ng&agrave;y 03 th&aacute;ng 09 năm 2020: Đội kỹ thuật Vn-Zoom triển khai tường lửa chống DDoS . Nhưng hacker lần n&agrave;y vẫn tấn c&ocirc;ng. Bị lộ IP m&aacute;y chủ, hacker bỏ qua tường lửa v&agrave; đ&aacute;nh thẳng v&agrave;o m&aacute;y chủ gốc. Hệ thống tiếp tục bị t&ecirc; liệt.&lt;br&gt;&lt;br&gt;Ngay l&uacute;c n&agrave;y m&igrave;nh phải kiểm tra lại to&agrave;n bộ c&aacute;c lỗ hổng t&igrave;m xem đ&acirc;u l&agrave; nơi để hacker khai th&aacute;c . M&ograve; tất cả từ server đến m&atilde; nguồn . M&atilde; nguồn Vn-Zoom mới được m&igrave;nh kiểm tra rất cẩn thận, kiểm duyệt kỹ trước khi triển khai n&ecirc;n kh&ocirc;ng thể c&oacute; backdoor từ code được. M&igrave;nh đ&atilde; c&oacute; ch&uacute;t kinh nghiệm từ Vn-Zoom.com cũ từng bị c&agrave;i m&atilde; độc ngay v&agrave;o trong m&atilde; nguồn , do vọc vạch c&aacute;c code kh&ocirc;ng ch&iacute;nh thức tr&ecirc;n mạng. ( Anh em kh&ocirc;ng am hiểu về code cần ch&uacute; &yacute; khi s&agrave;i code null hoặc Cr@ck)&lt;br&gt;&lt;br&gt;M&ograve; mẫm cuối c&ugrave;ng cũng ph&aacute;t hiện ra do trước đ&acirc;y triển khai một số dịch vụ thử nghiệm n&ecirc;n để lộ IP từ c&aacute;c dịch vụ n&agrave;y. Tuy nhi&ecirc;n , do việc thay đổi server l&uacute;c n&agrave;y sẽ khiến diễn đ&agrave;n bị gi&aacute;n đoạn , đội kỹ thuật tạm thời thay đổi phương &aacute;n. Bổ sung th&ecirc;m IP mới cho m&aacute;y chủ . L&uacute;c n&agrave;y m&aacute;y chủ sẽ chạy 2 IP, một IP sẽ được mở đường quốc tế , IP gốc tiếp tục chặn to&agrave;n bộ luồng quốc tế cho hacker đ&aacute;nh.&lt;br&gt;&lt;br&gt;Trong ng&agrave;y 03 th&aacute;ng 09 .2020 M&igrave;nh bắt đầu trỏ domain qua server ph&ograve;ng thủ chống DDoS. Thời gian đầu m&igrave;nh sử dụng g&oacute;i ph&ograve;ng thủ cơ bản v&agrave; mất ph&iacute; từ nh&agrave; cung cấp dịch vụ chống DDoS. Nhưng vẫn li&ecirc;n tục bị kẻ thủ &aacute;c đ&aacute;nh full request.&lt;br&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://upanh.vn-z.vn/images/2020/10/24/image_2020_09_04T09_18_13_062Z.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;br&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://upanh.vn-z.vn/images/2020/10/24/image_2020_09_04T14_14_37_192Z.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;br&gt;​&lt;/p&gt;&lt;p&gt;&lt;br&gt;Kẻ thủ &aacute;c thường đ&aacute;nh vượt qu&aacute; nhưng g&oacute;i ph&ograve;ng thủ cơ bản . Những g&oacute;i n&agrave;y chỉ lọc từ 1 triệu 2 triệu request/ng&agrave;y. Kẻ &aacute;c l&uacute;c n&agrave;y tiềm lực mạnh li&ecirc;n tục tấn c&ocirc;ng.&lt;br&gt;&lt;br&gt;Biển ph&aacute;p giải quyết t&igrave;nh huống tạm thời n&agrave;y vẫn chưa ổn v&igrave; M&aacute;y chủ gốc vẫn phải chặn quốc tế, điều n&agrave;y khiến một số chức năng của diễn đ&agrave;n lỗi . ( C&aacute;c thứ li&ecirc;n quan đến Facebook, Github, Google .v.v, lỗi )&lt;br&gt;&lt;br&gt;Server vẫn bị botnet h&agrave;ng ng&agrave;y&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://upanh.vn-z.vn/images/2020/10/24/image_2020_09_22T03_27_30_248Z.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;br&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://upanh.vn-z.vn/images/2020/10/24/image_2020_10_06T14_58_48_813Z.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;br&gt;&lt;br&gt;​&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;br&gt;- Đầu th&aacute;ng 10 , m&igrave;nh quyết định thử nghiệm tại m&aacute;y chủ mới . Tuy nhi&ecirc;n do di chuyển m&aacute;y chủ mới lại ph&aacute;t sinh c&aacute;c lỗi kh&ocirc;ng tương th&iacute;ch giữa hệ thống cũ v&agrave; hệ thống mới.&lt;br&gt;&lt;br&gt;Sau v&agrave;i đ&ecirc;m m&ograve; mẫm fix lỗi, m&igrave;nh bực qu&aacute; x&oacute;a to&agrave;n bộ v&agrave;i c&agrave;i mới ho&agrave;n to&agrave;n từ m&aacute;y chủ đến m&atilde; nguồn.&lt;br&gt;&lt;br&gt;- Ng&agrave;y 07/10 /2020 Chuyển hệ thống mới v&agrave; trỏ to&agrave;n bộ hệ thống qua Cloudflare ngay từ ban đầu.&lt;br&gt;&lt;br&gt;Thời gian đầu m&igrave;nh vẫn phải trực thủ c&ocirc;ng để ph&ograve;ng thủ , block bằng tay v&agrave; lọc IP , c&oacute; những ng&agrave;y phải thức đến s&aacute;ng .&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://upanh.vn-z.vn/images/2020/10/24/VNZdos.png&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;br&gt;​&lt;/p&gt;&lt;p&gt;Kẻ thủ &aacute;c vẫn tiếp tục tấn c&ocirc;ng từ nhiều hướng kh&aacute;c nhau. Nhưng phần lớn Request lớn vẫn đến từ Mỹ, Nga, Anh, Trung Quốc, &Uacute;c, Canada,Ph&aacute;p, Đức...&lt;br&gt;&lt;br&gt;Từ ng&agrave;y Sau khi bật Proxy to&agrave;n bộ c&aacute;c dịch vụ ch&iacute;nh , ẩn IP gốc của m&aacute;y chủ, setup pagerules, Firerules , lưu tượng tấn c&ocirc;ng của kẻ xấu đ&atilde; giảm đi nhiều.&lt;br&gt;&lt;br&gt;Kẻ thủ &aacute;c vẫn thi thoảng quay lại g&acirc;y rối, l&uacute;c n&agrave;y hệ thống Vn-Zoom cũng ổn định hơn, nếu kh&ocirc;ng chặn được th&igrave; tắt m&aacute;y đi ngủ , cho hacker n&oacute; đ&aacute;nh thoải m&aacute;i.&lt;br&gt;&lt;br&gt;&lt;strong&gt;II. Tại sao Vn-Zoom lại bị DDoS&lt;/strong&gt;&lt;br&gt;&lt;br&gt;&lt;strong&gt;1. Kẻ tấn c&ocirc;ng l&agrave; đơn vị c&oacute; tổ chức&lt;/strong&gt;&lt;br&gt;&lt;br&gt;Phương ph&aacute;p v&agrave; tiềm lực tấn c&ocirc;ng rất c&oacute; đầu tư. Hacker thu&ecirc; mạng lưới botnet chuy&ecirc;n nghiệp c&oacute; thể đ&aacute;nh sập ho&agrave;n to&agrave;n m&aacute;y chủ . D&ugrave; thực tế m&aacute;y chủ của Vn-Zoom c&oacute; thể chịu tải hơn 10.000 lượt connect realtime.&lt;br&gt;&lt;br&gt;Thời gian Vn-Zoom bị DDoS mạnh nhất tr&ugrave;ng hợp với một số trang mạng kh&aacute;c cũng bị tấn c&ocirc;ng. Phương thức tấn c&ocirc;ng kh&aacute; giống nhau, m&igrave;nh kh&ocirc;ng tiện n&oacute;i ở đ&acirc;y. C&aacute;c trang mạng n&agrave;y phần lớn đều phải chặn ho&agrave;n to&agrave;n c&aacute;c hướng tấn c&ocirc;ng từ quốc tế.&lt;br&gt;&lt;br&gt;Mục đ&iacute;ch của kẻ tấn c&ocirc;ng cản trở truy cập th&ocirc;ng tin nhằm g&acirc;y kh&oacute; khăn cho người đọc, th&agrave;nh vi&ecirc;n diễn đ&agrave;n, tiếp cận với c&aacute;c nội dung kh&ocirc;ng c&oacute; lợi cho &quot;kẻ tấn c&ocirc;ng&quot;.&lt;br&gt;&lt;br&gt;&lt;strong&gt;2. Kẻ tấn c&ocirc;ng l&agrave; những th&agrave;nh vi&ecirc;n bức x&uacute;c với diễn đ&agrave;n&lt;/strong&gt;&lt;br&gt;&lt;br&gt;M&igrave;nh kh&ocirc;ng nghĩ họ c&oacute; thể l&agrave;m, nhưng họ c&oacute; thể đi thu&ecirc;, nhưng với tiềm lực nhỏ . kỹ thuật chưa cao, họ c&oacute; thể chỉ đang test tool.&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://upanh.vn-z.vn/images/2020/03/20/vn-zoom.png&quot; alt=&quot;vn-z.vn&quot;&gt;&lt;/figure&gt;&lt;p&gt;&lt;a href=&quot;https://vn-z.vn/threads/dien-dan-vn-zoom-khong-coi-thanh-vien-ra-gi.19987/&quot;&gt;&quot;Diễn đ&agrave;n Vn-Zoom kh&ocirc;ng coi th&agrave;nh vi&ecirc;n ra g&igrave;&quot; - VN-Zoom | Cộng đồng Chia Sẻ Kiến Thức C&ocirc;ng Nghệ v&agrave; Phần Mềm M&aacute;y T&iacute;nh&lt;/a&gt;&lt;/p&gt;&lt;p&gt;Đ&oacute; l&agrave; tựa đề m&agrave; một bạn Mod trước đ&acirc;y được ban quản trị rất tin tưởng v&agrave; t&iacute;n nhiệm, giao v&agrave; quản l&yacute; khu vực HDH, sau một thời gian quản l&yacute; cũng thấy rất tốt về đạo đức cũng như tr&igrave;nh độ nghiệp vụ với sự năng nổ v&agrave; cống hiến. BQT rất vui v&igrave; điều đ&oacute;, v&agrave; lu&ocirc;n động vi&ecirc;n bạn ph&aacute;t triển kỹ năng hơn để...&lt;/p&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;https://vn-z.vn/styles/logo/favicon.png&quot; alt=&quot;vn-z.vn&quot;&gt;&lt;/figure&gt;&lt;p&gt;vn-z.vn&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;strong&gt;III. C&aacute;c biện ph&aacute;p xử l&yacute; v&agrave; một v&agrave;i kinh nghiệm nhỏ.&lt;/strong&gt;&lt;br&gt;&lt;br&gt;1. Biện ph&aacute;p xử l&yacute;&lt;br&gt;&lt;br&gt;- Về ph&aacute;p luật: Vn-Zoom hoạt động theo giấy ph&eacute;p mạng x&atilde; hội của BTTT m&igrave;nh c&oacute; thể tiếp tục truy cứu c&aacute;c đối tượng n&agrave;y ra cơ quan ph&aacute;p luật khi ph&aacute;t hiện ch&uacute;ng c&oacute; mặt tại Việt Nam&lt;br&gt;- Phần lớn c&aacute;c kẻ tấn c&ocirc;ng DDoS thường thu&ecirc; qua dịch vụ tấn c&ocirc;ng tại chợ đen n&ecirc;n việc truy t&igrave;m hung thủ thật sự rất kh&oacute; khăn , nhưng kh&ocirc;ng phải l&agrave; kh&ocirc;ng t&igrave;m ra được. Những c&aacute;i n&agrave;y li&ecirc;n quan đến tr&igrave;nh độ nghiệp vụ của người quản trị cũng như đơn vị an ninh mạng.&lt;br&gt;&lt;br&gt;2. Kinh nghiệm nhỏ.&lt;br&gt;&lt;br&gt;- Lu&ocirc;n ẩn IP gốc của m&aacute;y chủ qua hệ thống CDN hoặc Proxy chuy&ecirc;n nghiệp ngay từ khi triển khai hệ thống.&lt;br&gt;- C&oacute; thể d&ugrave;ng dịch vụ tường lửa miễn ph&iacute; của Cloudflare để ph&ograve;ng thủ với những cuộc tấn c&ocirc;ng nhỏ, hoặc lớn , nhưng thường l&agrave; phải chặn to&agrave;n bộ IP từ quốc tế.&lt;br&gt;&lt;br&gt;Qua đ&acirc;y thay mặt BQT Vn-Zoom c&aacute;m ơn tất cả anh chị em th&agrave;nh vi&ecirc;n đ&atilde; lu&ocirc;n đồng h&agrave;nh c&ugrave;ng diễn đ&agrave;n. C&aacute;c bạn l&agrave; động lực để ch&uacute;ng t&ocirc;i tiếp tục x&acirc;y dựng Vn-Zoom th&agrave;nh diễn đ&agrave;n c&ocirc;ng nghệ lớn mạnh, c&oacute; &iacute;ch.&lt;br&gt;&lt;br&gt;Rất xin lỗi thời gian qua v&igrave; kh&ocirc;ng kịp ngăn chặn DDoS khiến c&aacute;c bạn kh&ocirc;ng truy cập hoặc gặp kh&oacute; khăn trong qu&aacute; tr&igrave;nh sinh hoạt tại Vn-Zoom&lt;br&gt;&lt;br&gt;&lt;br&gt;Một v&agrave;i chia sẻ nho nhỏ. Nếu kẻ xấu tiếp tục tấn c&ocirc;ng m&igrave;nh sẽ tắt m&aacute;y chủ trước khi n&oacute; ddos.&lt;br&gt;&lt;br&gt;Update:&lt;br&gt;Kẻ thủ &aacute;c vẫn tiếp tục DDoS , nhưng phải đ&aacute;nh sập Firewall trước.&lt;/p&gt;&lt;p&gt;Nguồn: &lt;a href=&quot;https://vn-z.vn/threads/ky-su-ve-cuoc-tan-cong-vao-vn-zoom.29618/&quot;&gt;https://vn-z.vn/threads/ky-su-ve-cuoc-tan-cong-vao-vn-zoom.29618/&lt;/a&gt;&lt;/p&gt;', 'badge badge-success Share', 0, 0, 1, 2, 45, NULL),
(97, 'Món ăn ở cổng sau cũng khá phong phú', '2020-11-03 10:19:50', NULL, '&lt;p&gt;M&oacute;n ăn ở cổng sau cũng kh&aacute; phong ph&uacute;&lt;/p&gt;', '', 42, 1, 1, 1, 41, NULL),
(98, 'Thời gian ôn thi gấp rút', '2020-11-11 09:28:24', NULL, '&lt;p&gt;ch&agrave;o mọi người &hellip;&lt;/p&gt;', '', 1, 0, 1, 2, 44, 26);

